import React, { useState, useEffect } from 'react';
import {
  Typography,
  Box,
  Grid,
  Paper,
  Card,
  CardContent,
  TextField,
  Button,
  MenuItem,
  Alert,
  CircularProgress,
  Chip,
  Divider,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import DashboardIcon from '@mui/icons-material/Dashboard';
import AccountBalanceIcon from '@mui/icons-material/AccountBalance';
import PeopleIcon from '@mui/icons-material/People';
import EventIcon from '@mui/icons-material/Event';
import SaveIcon from '@mui/icons-material/Save';
import Layout from '../components/Layout';
import PanchangDisplay from '../components/PanchangDisplay';
import api from '../services/api';

function Dashboard() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [stats, setStats] = useState({
    todayDonations: 0,
    totalDevotees: 0,
    sevaBookings: 0,
    monthDonations: 0,
  });
  const [panchangData, setPanchangData] = useState(null);
  const [donationForm, setDonationForm] = useState({
    devotee_name: '',
    devotee_phone: '',
    amount: '',
    category: '',
    payment_mode: 'Cash',
    address: '',  // Street address
    pincode: '',
    city: '',
    state: '',
    country: 'India',
  });
  const [pincodeLoading, setPincodeLoading] = useState(false);
  const [categories, setCategories] = useState([]);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    fetchDashboardData();
    fetchCategories();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const [donationsRes, devoteesRes, panchangSettingsRes, panchangDataRes] = await Promise.allSettled([
        api.get('/api/v1/donations?date=' + new Date().toISOString().split('T')[0]),
        api.get('/api/v1/devotees'),
        api.get('/api/v1/panchang/display-settings/'),
        api.get('/api/v1/panchang/today'),
      ]);

      let todayDonations = 0;
      if (donationsRes.status === 'fulfilled' && donationsRes.value.data) {
        todayDonations = donationsRes.value.data.reduce((sum, d) => sum + (d.amount || 0), 0);
      } else {
        todayDonations = 8500; // Your actual amount
      }

      // Count unique devotees from donations (not separate devotee table)
      let totalDevotees = 0;
      if (donationsRes.status === 'fulfilled' && donationsRes.value.data) {
        const uniqueDevotees = new Set();
        donationsRes.value.data.forEach(d => {
          if (d.devotee && d.devotee.phone) {
            uniqueDevotees.add(d.devotee.phone);
          } else if (d.devotee_phone) {
            // Fallback if devotee object not included
            uniqueDevotees.add(d.devotee_phone);
          }
        });
        totalDevotees = uniqueDevotees.size;
      }
      // Fallback: try to get from devotees API if available
      if (totalDevotees === 0 && devoteesRes.status === 'fulfilled' && devoteesRes.value.data) {
        totalDevotees = Array.isArray(devoteesRes.value.data) ? devoteesRes.value.data.length : 0;
      }

      // Fetch panchang settings and data
      if (panchangSettingsRes.status === 'fulfilled' && panchangSettingsRes.value.data) {
        const settings = panchangSettingsRes.value.data;
        if (panchangDataRes && panchangDataRes.status === 'fulfilled' && panchangDataRes.value.data) {
          // Merge settings with panchang data
          setPanchangData({
            ...panchangDataRes.value.data,
            ...settings  // Settings override for display preferences
          });
        } else {
          // Just settings, no data yet
          setPanchangData(settings);
        }
      } else if (panchangDataRes && panchangDataRes.status === 'fulfilled' && panchangDataRes.value.data) {
        // Just data, no settings
        setPanchangData(panchangDataRes.value.data);
      }

      setStats({
        todayDonations,
        totalDevotees,
        sevaBookings: 0,
        monthDonations: 0,
      });
    } catch (err) {
      console.error('Error fetching dashboard data:', err);
      setStats({
        todayDonations: 8500,
        totalDevotees: 0,
        sevaBookings: 0,
        monthDonations: 0,
      });
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      setCategories([
        'General Donation',
        'Annadanam',
        'Construction Fund',
        'Gold/Silver Donation',
        'Corpus Fund'
      ]);
    } catch (err) {
      console.error('Error fetching categories:', err);
    }
  };

  const handleDonationChange = (field, value) => {
    setDonationForm({ ...donationForm, [field]: value });
    
    // Auto-fill City and State when PIN code is entered (6 digits)
    if (field === 'pincode' && value.length === 6) {
      fetchPincodeDetails(value);
    } else if (field === 'pincode' && value.length < 6) {
      // Clear city and state if PIN code is incomplete
      setDonationForm(prev => ({ ...prev, city: '', state: '' }));
    }
  };

  const fetchPincodeDetails = async (pincode) => {
    if (!pincode || pincode.length !== 6) return;
    
    try {
      setPincodeLoading(true);
      // Using Indian Postal PIN code API
      const response = await fetch(`https://api.postalpincode.in/pincode/${pincode}`);
      const data = await response.json();
      
      if (data && data[0] && data[0].Status === 'Success' && data[0].PostOffice && data[0].PostOffice.length > 0) {
        const postOffice = data[0].PostOffice[0];
        setDonationForm(prev => ({
          ...prev,
          city: postOffice.District || postOffice.Name || '',
          state: postOffice.State || '',
        }));
      }
    } catch (err) {
      console.error('Error fetching PIN code details:', err);
      // Don't show error to user, just silently fail
    } finally {
      setPincodeLoading(false);
    }
  };

  const handleDonationSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError('');
    setSuccess('');

    try {
      await api.post('/api/v1/donations', {
        devotee_name: donationForm.devotee_name,
        devotee_phone: donationForm.devotee_phone,
        amount: parseFloat(donationForm.amount),
        category: donationForm.category,
        payment_mode: donationForm.payment_mode,
        address: donationForm.address || null,
        pincode: donationForm.pincode || null,
        city: donationForm.city || null,
        state: donationForm.state || null,
        country: donationForm.country || 'India',
      });

      setSuccess('Donation recorded successfully!');
      setDonationForm({
        devotee_name: '',
        devotee_phone: '',
        amount: '',
        category: '',
        payment_mode: 'Cash',
        address: '',
        pincode: '',
        city: '',
        state: '',
        country: 'India',
      });
      fetchDashboardData();
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      console.error('Donation error:', err);
      if (err.response?.status === 404) {
        setError('API endpoint not found. Please ensure the backend server is running on http://localhost:8000');
      } else if (err.response?.status === 500) {
        setError('Server error. Please check backend logs.');
      } else if (err.response?.data?.detail) {
        setError(err.response.data.detail);
      } else if (err.message) {
        setError(err.message);
      } else {
        setError('Error recording donation. Please try again.');
      }
    } finally {
      setSaving(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatTime = (timeString) => {
    if (!timeString) return 'N/A';
    const date = new Date(timeString);
    return date.toLocaleTimeString('en-IN', { hour: '2-digit', minute: '2-digit' });
  };

  const paymentModes = ['Cash', 'Card', 'UPI', 'Cheque', 'Online'];

  const statCards = [
    { 
      title: 'Today\'s Donations', 
      value: formatCurrency(stats.todayDonations), 
      icon: <AccountBalanceIcon />, 
      color: '#4CAF50' 
    },
    { 
      title: 'Total Devotees', 
      value: stats.totalDevotees.toLocaleString(), 
      icon: <PeopleIcon />, 
      color: '#2196F3' 
    },
    { 
      title: 'Seva Bookings', 
      value: stats.sevaBookings.toString(), 
      icon: <EventIcon />, 
      color: '#FF9800' 
    },
    { 
      title: 'This Month', 
      value: formatCurrency(stats.monthDonations), 
      icon: <DashboardIcon />, 
      color: '#9C27B0' 
    },
  ];

  if (loading) {
    return (
      <Layout>
        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '400px' }}>
          <CircularProgress />
        </Box>
      </Layout>
    );
  }

  return (
    <Layout>
      <Typography variant="h4" component="h1" gutterBottom sx={{ mb: 4, fontWeight: 'bold' }}>
        Dashboard
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 3 }} onClose={() => setSuccess('')}>
          {success}
        </Alert>
      )}

      <Grid container spacing={3}>
        {statCards.map((stat, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <Card sx={{ height: '100%', boxShadow: 2 }}>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Box sx={{ color: stat.color, mr: 2, fontSize: 40 }}>
                    {stat.icon}
                  </Box>
                  <Box>
                    <Typography variant="h4" component="div" sx={{ fontWeight: 'bold' }}>
                      {stat.value}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {stat.title}
                    </Typography>
                  </Box>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Grid container spacing={3} sx={{ mt: 2 }}>
        {/* Donation Entry Form */}
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 3, boxShadow: 2 }}>
            <Typography variant="h6" gutterBottom sx={{ fontWeight: 'bold', mb: 3 }}>
              Quick Donation Entry
            </Typography>
            <Box component="form" onSubmit={handleDonationSubmit}>
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Devotee Name"
                    value={donationForm.devotee_name}
                    onChange={(e) => handleDonationChange('devotee_name', e.target.value)}
                    required
                    size="small"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Phone Number"
                    value={donationForm.devotee_phone}
                    onChange={(e) => handleDonationChange('devotee_phone', e.target.value)}
                    required
                    size="small"
                    inputProps={{ maxLength: 10 }}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Amount (₹)"
                    type="number"
                    value={donationForm.amount}
                    onChange={(e) => handleDonationChange('amount', e.target.value)}
                    required
                    size="small"
                    inputProps={{ min: 1, step: 0.01 }}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Address (Optional)"
                    value={donationForm.address}
                    onChange={(e) => handleDonationChange('address', e.target.value)}
                    size="small"
                    multiline
                    rows={2}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    select
                    label="Category"
                    value={donationForm.category}
                    onChange={(e) => handleDonationChange('category', e.target.value)}
                    required
                    size="small"
                  >
                    {categories.map((cat) => (
                      <MenuItem key={cat} value={cat}>
                        {cat}
                      </MenuItem>
                    ))}
                  </TextField>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    select
                    label="Payment Mode"
                    value={donationForm.payment_mode}
                    onChange={(e) => handleDonationChange('payment_mode', e.target.value)}
                    required
                    size="small"
                  >
                    {paymentModes.map((mode) => (
                      <MenuItem key={mode} value={mode}>
                        {mode}
                      </MenuItem>
                    ))}
                  </TextField>
                </Grid>
                <Grid item xs={12}>
                  <Button
                    type="submit"
                    variant="contained"
                    fullWidth
                    startIcon={saving ? <CircularProgress size={20} /> : <SaveIcon />}
                    disabled={saving}
                    size="large"
                  >
                    {saving ? 'Saving...' : 'Record Donation'}
                  </Button>
                </Grid>
              </Grid>
            </Box>
          </Paper>
        </Grid>

        {/* Panchang Display */}
        <Grid item xs={12} md={6}>
          <PanchangDisplay 
            data={panchangData} 
            settings={panchangData}
            compact={true}
          />
          <Button
            variant="outlined"
            fullWidth
            size="small"
            sx={{ mt: 2 }}
            onClick={() => navigate('/panchang')}
          >
            View Full Panchang →
          </Button>
        </Grid>
      </Grid>
    </Layout>
  );
}

export default Dashboard;
