/**
 * Donation Service
 */

import api from './api';

export const donationService = {
  /**
   * Create a new donation
   */
  async createDonation(donationData) {
    const response = await api.post('/donations', donationData);
    return response.data;
  },

  /**
   * Get list of donations
   */
  async getDonations(params = {}) {
    const response = await api.get('/donations', { params });
    return response.data;
  },

  /**
   * Get single donation by ID
   */
  async getDonation(id) {
    const response = await api.get(`/donations/${id}`);
    return response.data;
  },

  /**
   * Get daily report
   */
  async getDailyReport(date) {
    const response = await api.get('/donations/report/daily', {
      params: { report_date: date },
    });
    return response.data;
  },

  /**
   * Get donation categories
   */
  async getCategories() {
    const response = await api.get('/donations/categories');
    return response.data;
  },

  /**
   * Create donation category
   */
  async createCategory(categoryData) {
    const response = await api.post('/donations/categories', categoryData);
    return response.data;
  },
};
