/**
 * Panchang Widget Component
 * Displays today's panchang in a compact card format
 * Based on Panchang_Display_Guide.md
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Box,
  Chip,
  CircularProgress,
  Alert,
  Grid,
} from '@mui/material';
import {
  WbSunny as SunriseIcon,
  WbTwilight as SunsetIcon,
  Warning as WarningIcon,
  CheckCircle as CheckCircleIcon,
} from '@mui/icons-material';
import panchangService from '../services/panchangService';

const PanchangWidget = ({ city = 'Bangalore' }) => {
  const [panchang, setPanchang] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadPanchang();
  }, [city]);

  const loadPanchang = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await panchangService.getTodayPanchang(city);
      setPanchang(data);
    } catch (err) {
      console.error('Error loading panchang:', err);
      setError('Failed to load panchang data');
    } finally {
      setLoading(false);
    }
  };

  const formatTime = (timeString) => {
    if (!timeString) return 'N/A';
    try {
      const date = new Date(timeString);
      return date.toLocaleTimeString('en-IN', {
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
        hour12: true,
      });
    } catch {
      return timeString;
    }
  };
  
  const formatTimeShort = (timeString) => {
    if (!timeString) return 'N/A';
    try {
      const date = new Date(timeString);
      return date.toLocaleTimeString('en-IN', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      });
    } catch {
      return timeString;
    }
  };

  const getNakshatraColor = (nakshatra) => {
    if (nakshatra?.is_auspicious) return 'success';
    if (nakshatra?.is_inauspicious) return 'error';
    return 'default';
  };

  const getTithiBadge = (tithi) => {
    if (tithi?.is_ekadashi) return { label: '‚≠ê EKADASHI', color: 'warning' };
    if (tithi?.is_purnima) return { label: 'üåï PURNIMA', color: 'info' };
    if (tithi?.is_amavasya) return { label: 'üåë AMAVASYA', color: 'default' };
    return null;
  };

  const getNakshatraStars = (nakshatra) => {
    if (!nakshatra) return '';
    if (nakshatra.is_auspicious) {
      // Check if it's one of the most auspicious
      const mostAuspicious = ['Pushya', 'Rohini', 'Hasta', 'Shravana', 'Revati'];
      if (mostAuspicious.includes(nakshatra.name)) {
        return '‚≠ê‚≠ê‚≠ê';
      }
      return '‚≠ê‚≠ê';
    }
    if (nakshatra.is_inauspicious) {
      return '‚ö†Ô∏è';
    }
    return '';
  };

  if (loading) {
    return (
      <Card>
        <CardContent>
          <Box display="flex" justifyContent="center" alignItems="center" minHeight={200}>
            <CircularProgress />
          </Box>
        </CardContent>
      </Card>
    );
  }

  if (error) {
    return (
      <Card>
        <CardContent>
          <Alert severity="error">{error}</Alert>
        </CardContent>
      </Card>
    );
  }

  if (!panchang) {
    return null;
  }

  const tithiBadge = getTithiBadge(panchang.tithi);
  const rahuKaalStart = formatTime(panchang.rahu_kaal?.start);
  const rahuKaalEnd = formatTime(panchang.rahu_kaal?.end);
  const abhijitStart = formatTime(panchang.abhijit_muhurat?.start);
  const abhijitEnd = formatTime(panchang.abhijit_muhurat?.end);

  return (
    <Card>
      <CardContent>
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
          <Typography variant="h6" component="h2">
            üìÖ Today's Panchang
          </Typography>
          <Chip
            label={city}
            size="small"
            color="primary"
            variant="outlined"
          />
        </Box>

        <Typography variant="body2" color="text.secondary" gutterBottom>
          {new Date(panchang.date).toLocaleDateString('en-IN', {
            weekday: 'long',
            year: 'numeric',
            month: 'long',
            day: 'numeric',
          })}
        </Typography>

        <Box mt={2} mb={2}>
          <Grid container spacing={2}>
            {/* Tithi */}
            <Grid item xs={12} sm={6}>
              <Box
                border={1}
                borderColor="divider"
                borderRadius={1}
                p={1.5}
                bgcolor="background.paper"
              >
                <Typography variant="subtitle2" fontWeight="bold" gutterBottom>
                  TITHI (‡§§‡§ø‡§•‡§ø)
                </Typography>
                <Typography variant="body2" fontWeight="medium">
                  {panchang.tithi?.full_name || `${panchang.tithi?.paksha} ${panchang.tithi?.name}`}
                </Typography>
                {panchang.tithi?.end_time && (
                  <Typography variant="caption" color="text.secondary" display="block" mt={0.5}>
                    Until: {formatTimeShort(panchang.tithi.end_time)}
                  </Typography>
                )}
                {panchang.tithi?.next_tithi && (
                  <Typography variant="caption" color="text.secondary" display="block">
                    Then: {panchang.tithi.next_tithi}
                  </Typography>
                )}
                {tithiBadge && (
                  <Chip
                    label={tithiBadge.label}
                    size="small"
                    color={tithiBadge.color}
                    sx={{ mt: 0.5 }}
                  />
                )}
              </Box>
            </Grid>

            {/* Nakshatra */}
            <Grid item xs={12} sm={6}>
              <Box
                border={1}
                borderColor="divider"
                borderRadius={1}
                p={1.5}
                bgcolor="background.paper"
              >
                <Typography variant="subtitle2" fontWeight="bold" gutterBottom>
                  NAKSHATRA (‡§®‡§ï‡•ç‡§∑‡§§‡•ç‡§∞)
                </Typography>
                <Box display="flex" alignItems="center" gap={1} flexWrap="wrap">
                  <Typography variant="body2" fontWeight="medium">
                    {panchang.nakshatra?.name} {getNakshatraStars(panchang.nakshatra)}
                  </Typography>
                  <Chip
                    label={`Pada ${panchang.nakshatra?.pada}`}
                    size="small"
                    variant="outlined"
                  />
                  {panchang.nakshatra?.is_auspicious && (
                    <Chip
                      label="Auspicious"
                      size="small"
                      color="success"
                    />
                  )}
                  {panchang.nakshatra?.is_inauspicious && (
                    <Chip
                      label="Inauspicious"
                      size="small"
                      color="error"
                    />
                  )}
                </Box>
                {panchang.nakshatra?.end_time && (
                  <Typography variant="caption" color="text.secondary" display="block" mt={0.5}>
                    Until: {formatTimeShort(panchang.nakshatra.end_time)}
                  </Typography>
                )}
                {panchang.nakshatra?.next_nakshatra && (
                  <Typography variant="caption" color="text.secondary" display="block">
                    Then: {panchang.nakshatra.next_nakshatra}
                  </Typography>
                )}
                {panchang.nakshatra?.auspiciousness_note && (
                  <Typography variant="caption" color="text.secondary" display="block" mt={0.5}>
                    {panchang.nakshatra.auspiciousness_note}
                  </Typography>
                )}
              </Box>
            </Grid>
          </Grid>
        </Box>
        
        {/* Hindu Calendar Info */}
        {panchang.samvat && (
          <Box
            border={1}
            borderColor="divider"
            borderRadius={1}
            p={1.5}
            mb={2}
            bgcolor="background.paper"
          >
            <Typography variant="subtitle2" fontWeight="bold" gutterBottom>
              HINDU CALENDAR
            </Typography>
            <Box display="flex" gap={2} flexWrap="wrap">
              <Typography variant="body2">
                <strong>Vikram Samvat:</strong> {panchang.samvat?.vikram_samvat}
              </Typography>
              <Typography variant="body2">
                <strong>Shaka Samvat:</strong> {panchang.samvat?.shaka_samvat}
              </Typography>
              {panchang.samvatsara && (
                <Typography variant="body2">
                  <strong>Samvatsara:</strong> {panchang.samvatsara}
                </Typography>
              )}
              {panchang.hindu_month && (
                <>
                  <Typography variant="body2">
                    <strong>Purnimanta:</strong> {panchang.hindu_month?.purnimanta_month}
                  </Typography>
                  <Typography variant="body2">
                    <strong>Amanta:</strong> {panchang.hindu_month?.amanta_month}
                  </Typography>
                </>
              )}
              {panchang.ritu && (
                <Typography variant="body2">
                  <strong>Ritu:</strong> {panchang.ritu?.ritu_english} ({panchang.ritu?.ritu_sanskrit})
                </Typography>
              )}
              {panchang.ayana && (
                <Typography variant="body2">
                  <strong>Ayana:</strong> {panchang.ayana}
                </Typography>
              )}
              {panchang.vara && (
                <Typography variant="body2">
                  <strong>Day:</strong> {panchang.vara?.day_name} ({panchang.vara?.day_sanskrit}) | Planet: {panchang.vara?.ruling_planet}
                </Typography>
              )}
            </Box>
          </Box>
        )}
        
        {/* Special Days */}
        {panchang.special_days && panchang.special_days.length > 0 && (
          <Box
            border={1}
            borderColor="warning.main"
            borderRadius={1}
            p={1.5}
            mb={2}
            bgcolor="warning.light"
            sx={{ opacity: 0.1 }}
          >
            <Typography variant="subtitle2" fontWeight="bold" color="warning.main" gutterBottom>
              üéâ SPECIAL DAYS
            </Typography>
            {panchang.special_days.map((day, idx) => (
              <Box key={idx} mb={0.5}>
                <Typography variant="body2" fontWeight="medium" color="warning.main">
                  ‚Ä¢ {day.name}
                </Typography>
                <Typography variant="caption" color="text.secondary">
                  {day.description}
                </Typography>
              </Box>
            ))}
          </Box>
        )}

        {/* Sun Timings */}
        <Box
          border={1}
          borderColor="divider"
          borderRadius={1}
          p={1.5}
          mb={2}
          bgcolor="background.paper"
        >
          <Typography variant="subtitle2" fontWeight="bold" gutterBottom>
            SUN TIMINGS
          </Typography>
          <Box display="flex" alignItems="center" gap={2} flexWrap="wrap">
            <Box display="flex" alignItems="center" gap={0.5}>
              <SunriseIcon color="warning" fontSize="small" />
              <Typography variant="body2">
                Sunrise: {formatTime(panchang.sunrise)}
              </Typography>
            </Box>
            <Box display="flex" alignItems="center" gap={0.5}>
              <SunsetIcon color="warning" fontSize="small" />
              <Typography variant="body2">
                Sunset: {formatTime(panchang.sunset)}
              </Typography>
            </Box>
          </Box>
        </Box>

        {/* Rahu Kaal Warning */}
        {panchang.rahu_kaal && (
          <Box
            border={1}
            borderColor="error.main"
            borderRadius={1}
            p={1.5}
            mb={2}
            bgcolor="error.light"
            sx={{ opacity: 0.1 }}
          >
            <Box display="flex" alignItems="center" gap={1} mb={1}>
              <WarningIcon color="error" fontSize="small" />
              <Typography variant="subtitle2" fontWeight="bold" color="error">
                ‚ö†Ô∏è RAHU KAAL (INAUSPICIOUS)
              </Typography>
            </Box>
            <Typography variant="body2" color="error">
              {rahuKaalStart} - {rahuKaalEnd}
            </Typography>
            <Typography variant="caption" color="text.secondary" display="block" mt={0.5}>
              Avoid: New work, meetings, travel, financial transactions
            </Typography>
          </Box>
        )}

        {/* Abhijit Muhurat */}
        {panchang.abhijit_muhurat && (
          <Box
            border={1}
            borderColor="success.main"
            borderRadius={1}
            p={1.5}
            bgcolor="success.light"
            sx={{ opacity: 0.1 }}
          >
            <Box display="flex" alignItems="center" gap={1} mb={1}>
              <CheckCircleIcon color="success" fontSize="small" />
              <Typography variant="subtitle2" fontWeight="bold" color="success.main">
                ‚úÖ ABHIJIT MUHURAT (MOST AUSPICIOUS)
              </Typography>
            </Box>
            <Typography variant="body2" color="success.main">
              {abhijitStart} - {abhijitEnd}
            </Typography>
            <Typography variant="caption" color="text.secondary" display="block" mt={0.5}>
              Best time for all auspicious activities
            </Typography>
          </Box>
        )}

        {/* Yoga and Karana */}
        <Box mt={2} display="flex" gap={1} flexWrap="wrap">
          <Chip
            label={`Yoga: ${panchang.yoga?.name}`}
            size="small"
            variant="outlined"
          />
          <Chip
            label={`Karana: ${panchang.karana?.name}`}
            size="small"
            variant="outlined"
          />
        </Box>
      </CardContent>
    </Card>
  );
};

export default PanchangWidget;
