/**
 * Panchang Widget Component
 * Enhanced display based on Panchang_Display_Guide.md
 * Shows comprehensive Vedic Panchang with proper styling and formatting
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Box,
  Chip,
  CircularProgress,
  Alert,
  Grid,
  Divider,
  Paper,
} from '@mui/material';
import {
  WbSunny as SunriseIcon,
  WbTwilight as SunsetIcon,
  Warning as WarningIcon,
  CheckCircle as CheckCircleIcon,
  Star as StarIcon,
  Brightness2 as MoonIcon,
} from '@mui/icons-material';
import panchangService from '../services/panchangService';

const PanchangWidget = ({ city = 'Bengaluru' }) => {
  const [panchang, setPanchang] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Detect regional language based on city/state
    const detectedLang = getLanguageFromLocation(city, state);
    setRegionalLang(detectedLang);
    
    loadPanchang();
    // Refresh every minute to catch transitions
    const interval = setInterval(loadPanchang, 60000);
    return () => clearInterval(interval);
  }, [city, state]);

  const loadPanchang = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await panchangService.getTodayPanchang(city);
      setPanchang(data);
    } catch (err) {
      console.error('Error loading panchang:', err);
      setError('Failed to load panchang data. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const formatTime = (timeString) => {
    if (!timeString) return 'N/A';
    try {
      // Handle both ISO strings and HH:MM:SS format
      let date;
      if (timeString.includes('T') || timeString.includes(':')) {
        // Try parsing as ISO or time string
        if (timeString.includes('T')) {
          date = new Date(timeString);
        } else {
          // HH:MM:SS format - create date for today
          const [hours, minutes, seconds] = timeString.split(':');
          date = new Date();
          date.setHours(parseInt(hours), parseInt(minutes), parseInt(seconds || 0));
        }
      } else {
        return timeString;
      }
      
      return date.toLocaleTimeString('en-IN', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
      });
    } catch {
      return timeString;
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('en-IN', {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
      });
    } catch {
      return dateString;
    }
  };

  const getNakshatraQuality = (nakshatra) => {
    if (!nakshatra) return { stars: '', color: 'default', label: '' };
    
    const mostAuspicious = ['Pushya', 'Rohini', 'Hasta', 'Shravana', 'Revati'];
    const inauspicious = ['Mula', 'Ardra', 'Ashlesha'];
    
    if (mostAuspicious.includes(nakshatra.name)) {
      return { stars: '‚≠ê‚≠ê‚≠ê', color: 'success', label: 'MOST AUSPICIOUS' };
    }
    if (nakshatra.is_auspicious) {
      return { stars: '‚≠ê‚≠ê', color: 'success', label: 'AUSPICIOUS' };
    }
    if (inauspicious.includes(nakshatra.name) || nakshatra.is_inauspicious) {
      return { stars: '‚ö†Ô∏è', color: 'error', label: 'INAUSPICIOUS' };
    }
    return { stars: '‚≠ê', color: 'default', label: 'NEUTRAL' };
  };

  const getTithiBadge = (tithi) => {
    if (!tithi) return null;
    if (tithi.is_ekadashi) return { label: '‚≠ê EKADASHI', color: 'warning', icon: 'üçÉ' };
    if (tithi.is_purnima) return { label: 'üåï PURNIMA', color: 'info', icon: 'üåï' };
    if (tithi.is_amavasya) return { label: 'üåë AMAVASYA', color: 'default', icon: 'üåë' };
    return null;
  };

  if (loading) {
    return (
      <Card sx={{ mb: 4 }}>
        <CardContent>
          <Box display="flex" justifyContent="center" alignItems="center" minHeight={200}>
            <CircularProgress />
            <Typography variant="h6" sx={{ ml: 2 }}>Loading Panchang...</Typography>
          </Box>
        </CardContent>
      </Card>
    );
  }

  if (error) {
    return (
      <Card sx={{ mb: 4 }}>
        <CardContent>
          <Alert severity="error">{error}</Alert>
        </CardContent>
      </Card>
    );
  }

  if (!panchang) {
    return null;
  }

  const tithiBadge = getTithiBadge(panchang.tithi);
  const nakshatraQuality = getNakshatraQuality(panchang.nakshatra);

  return (
    <Card 
      sx={{ 
        mb: 4, 
        bgcolor: '#f9f9f9',
        borderLeft: '5px solid #1976d2',
        boxShadow: 3
      }}
    >
      <CardContent>
        {/* Header */}
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
          <Typography 
            variant="h5" 
            sx={{ 
              color: '#1976d2', 
              fontWeight: 'bold',
              display: 'flex',
              alignItems: 'center',
              gap: 1
            }}
          >
            üìÖ {formatDualLang('todayPanchang', regionalLang)}
          </Typography>
          <Chip
            label={panchang.location?.city || city}
            size="small"
            color="primary"
            variant="outlined"
          />
        </Box>

        <Typography variant="subtitle1" color="text.secondary" gutterBottom>
          {formatDate(panchang.date)}
        </Typography>

        {/* Hindu Calendar Info */}
        {panchang.samvat && (
          <Box
            sx={{
              p: 1.5,
              mb: 2,
              bgcolor: '#fff3cd',
              borderRadius: 1,
              border: '1px solid #ffc107'
            }}
          >
            <Typography variant="caption" fontWeight="bold" display="block" gutterBottom>
              {formatDualLang('hinduCalendar', regionalLang)}
            </Typography>
            <Box display="flex" gap={2} flexWrap="wrap">
              {panchang.samvat.vikram && (
                <Typography variant="body2">
                  <strong>{formatDualLang('vikramSamvat', regionalLang)}:</strong> {panchang.samvat.vikram}
                </Typography>
              )}
              {panchang.samvat.shaka && (
                <Typography variant="body2">
                  <strong>{formatDualLang('shakaSamvat', regionalLang)}:</strong> {panchang.samvat.shaka}
                </Typography>
              )}
              {panchang.samvat.samvatsara && (
                <Typography variant="body2">
                  <strong>{formatDualLang('samvatsara', regionalLang)}:</strong> {panchang.samvat.samvatsara}
                </Typography>
              )}
              {panchang.masa && (
                <>
                  {panchang.masa.purnimanta && (
                    <Typography variant="body2">
                      <strong>{formatDualLang('purnimanta', regionalLang)}:</strong> {panchang.masa.purnimanta}
                    </Typography>
                  )}
                  {panchang.masa.amanta && (
                    <Typography variant="body2">
                      <strong>{formatDualLang('amanta', regionalLang)}:</strong> {panchang.masa.amanta}
                    </Typography>
                  )}
                </>
              )}
              {panchang.paksha && (
                <Typography variant="body2">
                  <strong>{formatDualLang('paksha', regionalLang)}:</strong> {panchang.paksha}
                </Typography>
              )}
              {panchang.ritu && (
                <Typography variant="body2">
                  <strong>{formatDualLang('ritu', regionalLang)}:</strong> {panchang.ritu.english || panchang.ritu.name} ({panchang.ritu.sanskrit})
                </Typography>
              )}
              {panchang.ayana && (
                <Typography variant="body2">
                  <strong>{formatDualLang('ayana', regionalLang)}:</strong> {panchang.ayana.name || panchang.ayana} ({panchang.ayana.sanskrit})
                </Typography>
              )}
            </Box>
          </Box>
        )}

        {/* Day Information */}
        {panchang.vara && (
          <Box sx={{ mb: 2 }}>
            <Typography variant="body2" color="text.secondary">
              <strong>Day:</strong> {panchang.vara.english || panchang.vara.name} ({panchang.vara.sanskrit || panchang.vara.hindi})
              {panchang.vara.ruling_planet && ` | Ruling Planet: ${panchang.vara.ruling_planet}`}
            </Typography>
          </Box>
        )}

        {/* The Five Limbs (Panch-Anga) */}
        <Typography variant="h6" sx={{ mt: 3, mb: 2, color: '#3f51b5', fontWeight: 'bold' }}>
          {formatDualLang('fiveLimbs', regionalLang)}
        </Typography>

        <Grid container spacing={2} sx={{ mb: 3 }}>
          {/* 1. TITHI */}
          <Grid item xs={12} sm={6} md={4}>
            <Paper
              elevation={2}
              sx={{
                p: 2,
                bgcolor: '#fff',
                border: '1px solid #e0e0e0',
                borderRadius: 2,
                height: '100%'
              }}
            >
              <Typography variant="subtitle1" fontWeight="bold" gutterBottom sx={{ color: '#3f51b5' }}>
                {formatDualLang('tithi', regionalLang)}
              </Typography>
              <Typography variant="body1" fontWeight="medium" gutterBottom>
                {panchang.tithi?.full_name || `${panchang.tithi?.paksha} ${panchang.tithi?.name}`}
              </Typography>
              {panchang.tithi?.end_time_formatted && (
                <Typography variant="caption" color="text.secondary" display="block">
                  {formatDualLang('until', regionalLang)}: {formatTime(panchang.tithi.end_time_formatted)}
                </Typography>
              )}
              {panchang.tithi?.end_time && (
                <Typography variant="caption" color="text.secondary" display="block">
                  {formatDualLang('until', regionalLang)}: {formatTime(panchang.tithi.end_time)}
                </Typography>
              )}
              {panchang.tithi?.next_tithi && (
                <Typography variant="caption" color="text.secondary" display="block">
                  {formatDualLang('then', regionalLang)}: {panchang.tithi.next_tithi}
                </Typography>
              )}
              {tithiBadge && (
                <Chip
                  label={tithiBadge.label}
                  size="small"
                  color={tithiBadge.color}
                  sx={{ mt: 1 }}
                />
              )}
            </Paper>
          </Grid>

          {/* 2. NAKSHATRA */}
          <Grid item xs={12} sm={6} md={4}>
            <Paper
              elevation={2}
              sx={{
                p: 2,
                bgcolor: '#fff',
                border: '1px solid #e0e0e0',
                borderRadius: 2,
                height: '100%'
              }}
            >
              <Typography variant="subtitle1" fontWeight="bold" gutterBottom sx={{ color: '#3f51b5' }}>
                NAKSHATRA (‡§®‡§ï‡•ç‡§∑‡§§‡•ç‡§∞)
              </Typography>
              <Box display="flex" alignItems="center" gap={1} flexWrap="wrap" mb={1}>
                <Typography variant="body1" fontWeight="medium">
                  {panchang.nakshatra?.name} {nakshatraQuality.stars}
                </Typography>
                {panchang.nakshatra?.pada && (
                  <Chip
                    label={`Pada ${panchang.nakshatra.pada}`}
                    size="small"
                    variant="outlined"
                  />
                )}
              </Box>
              {nakshatraQuality.label && (
                <Chip
                  label={nakshatraQuality.label}
                  size="small"
                  color={nakshatraQuality.color}
                  sx={{ mb: 1 }}
                />
              )}
              {panchang.nakshatra?.end_time_formatted && (
                <Typography variant="caption" color="text.secondary" display="block" mt={1}>
                  Until: {formatTime(panchang.nakshatra.end_time_formatted)}
                </Typography>
              )}
              {panchang.nakshatra?.end_time && (
                <Typography variant="caption" color="text.secondary" display="block" mt={1}>
                  Until: {formatTime(panchang.nakshatra.end_time)}
                </Typography>
              )}
              {panchang.nakshatra?.next_nakshatra && (
                <Typography variant="caption" color="text.secondary" display="block">
                  Then: {panchang.nakshatra.next_nakshatra}
                </Typography>
              )}
              {panchang.nakshatra?.auspiciousness_note && (
                <Typography variant="caption" color="text.secondary" display="block" mt={0.5}>
                  {panchang.nakshatra.auspiciousness_note}
                </Typography>
              )}
            </Paper>
          </Grid>

          {/* 3. YOGA */}
          <Grid item xs={12} sm={6} md={4}>
            <Paper
              elevation={2}
              sx={{
                p: 2,
                bgcolor: '#fff',
                border: '1px solid #e0e0e0',
                borderRadius: 2,
                height: '100%'
              }}
            >
              <Typography variant="subtitle1" fontWeight="bold" gutterBottom sx={{ color: '#3f51b5' }}>
                YOGA (‡§Ø‡•ã‡§ó)
              </Typography>
              <Typography variant="body1" fontWeight="medium">
                {panchang.yoga?.name}
              </Typography>
              {panchang.yoga?.end_time_formatted && (
                <Typography variant="caption" color="text.secondary" display="block" mt={1}>
                  Until: {formatTime(panchang.yoga.end_time_formatted)}
                </Typography>
              )}
              {panchang.yoga?.end_time && (
                <Typography variant="caption" color="text.secondary" display="block" mt={1}>
                  Until: {formatTime(panchang.yoga.end_time)}
                </Typography>
              )}
            </Paper>
          </Grid>

          {/* 4. KARANA */}
          <Grid item xs={12} sm={6} md={4}>
            <Paper
              elevation={2}
              sx={{
                p: 2,
                bgcolor: '#fff',
                border: '1px solid #e0e0e0',
                borderRadius: 2,
                height: '100%'
              }}
            >
              <Typography variant="subtitle1" fontWeight="bold" gutterBottom sx={{ color: '#3f51b5' }}>
                KARANA (‡§ï‡§∞‡§£)
              </Typography>
              {panchang.karana?.first_half && (
                <Box mb={1}>
                  <Typography variant="body2" fontWeight="medium">
                    First Half: {panchang.karana.first_half.name}
                  </Typography>
                  {panchang.karana.first_half.end_time && (
                    <Typography variant="caption" color="text.secondary" display="block">
                      Until: {formatTime(panchang.karana.first_half.end_time)}
                    </Typography>
                  )}
                </Box>
              )}
              {panchang.karana?.second_half && (
                <Box>
                  <Typography variant="body2" fontWeight="medium">
                    Second Half: {panchang.karana.second_half.name}
                  </Typography>
                  {panchang.karana.second_half.end_time && (
                    <Typography variant="caption" color="text.secondary" display="block">
                      Until: {formatTime(panchang.karana.second_half.end_time)}
                    </Typography>
                  )}
                </Box>
              )}
              {panchang.karana?.is_bhadra && (
                <Chip
                  label="‚ö†Ô∏è BHADRA (Inauspicious)"
                  size="small"
                  color="error"
                  sx={{ mt: 1 }}
                />
              )}
            </Paper>
          </Grid>

          {/* 5. VARA */}
          {panchang.vara && (
            <Grid item xs={12} sm={6} md={4}>
              <Paper
                elevation={2}
                sx={{
                  p: 2,
                  bgcolor: '#fff',
                  border: '1px solid #e0e0e0',
                  borderRadius: 2,
                  height: '100%'
                }}
              >
                <Typography variant="subtitle1" fontWeight="bold" gutterBottom sx={{ color: '#3f51b5' }}>
                  VARA (‡§µ‡§æ‡§∞)
                </Typography>
                <Typography variant="body1" fontWeight="medium">
                  {panchang.vara.english || panchang.vara.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {panchang.vara.sanskrit || panchang.vara.hindi}
                </Typography>
                {panchang.vara.ruling_planet && (
                  <Typography variant="caption" color="text.secondary" display="block" mt={0.5}>
                    Ruling Planet: {panchang.vara.ruling_planet}
                  </Typography>
                )}
              </Paper>
            </Grid>
          )}
        </Grid>

        <Divider sx={{ my: 3 }} />

        {/* Sun & Moon Timings */}
        <Typography variant="h6" sx={{ mb: 2, color: '#3f51b5', fontWeight: 'bold' }}>
          SUN & MOON TIMINGS
        </Typography>
        <Paper
          elevation={2}
          sx={{
            p: 2,
            bgcolor: '#fff',
            border: '1px solid #e0e0e0',
            borderRadius: 2,
            mb: 3
          }}
        >
          <Grid container spacing={2}>
            <Grid item xs={6} sm={3}>
              <Box display="flex" alignItems="center" gap={1}>
                <SunriseIcon color="warning" />
                <Box>
                  <Typography variant="caption" color="text.secondary">Sunrise</Typography>
                  <Typography variant="body2" fontWeight="medium">
                    {formatTime(panchang.sunrise)}
                  </Typography>
                </Box>
              </Box>
            </Grid>
            <Grid item xs={6} sm={3}>
              <Box display="flex" alignItems="center" gap={1}>
                <SunsetIcon color="warning" />
                <Box>
                  <Typography variant="caption" color="text.secondary">Sunset</Typography>
                  <Typography variant="body2" fontWeight="medium">
                    {formatTime(panchang.sunset)}
                  </Typography>
                </Box>
              </Box>
            </Grid>
            {panchang.moonrise && (
              <Grid item xs={6} sm={3}>
                <Box display="flex" alignItems="center" gap={1}>
                  <MoonIcon color="primary" />
                  <Box>
                    <Typography variant="caption" color="text.secondary">Moonrise</Typography>
                    <Typography variant="body2" fontWeight="medium">
                      {formatTime(panchang.moonrise)}
                    </Typography>
                  </Box>
                </Box>
              </Grid>
            )}
            {panchang.moonset && (
              <Grid item xs={6} sm={3}>
                <Box display="flex" alignItems="center" gap={1}>
                  <MoonIcon color="primary" />
                  <Box>
                    <Typography variant="caption" color="text.secondary">Moonset</Typography>
                    <Typography variant="body2" fontWeight="medium">
                      {formatTime(panchang.moonset)}
                    </Typography>
                  </Box>
                </Box>
              </Grid>
            )}
          </Grid>
        </Paper>

        {/* Inauspicious Times - VERY PROMINENT */}
        <Typography variant="h6" sx={{ mb: 2, color: '#d32f2f', fontWeight: 'bold' }}>
          ‚ö†Ô∏è INAUSPICIOUS TIMES - AVOID THESE
        </Typography>
        <Grid container spacing={2} sx={{ mb: 3 }}>
          {panchang.rahu_kaal && (
            <Grid item xs={12} sm={4}>
              <Paper
                elevation={2}
                sx={{
                  p: 2,
                  bgcolor: '#ffebee',
                  border: '2px solid #d32f2f',
                  borderRadius: 2
                }}
              >
                <Box display="flex" alignItems="center" gap={1} mb={1}>
                  <WarningIcon color="error" />
                  <Typography variant="subtitle2" fontWeight="bold" color="error">
                    RAHU KAAL (‡§∞‡§æ‡§π‡•Å ‡§ï‡§æ‡§≤)
                  </Typography>
                </Box>
                <Typography variant="body1" fontWeight="bold" color="error">
                  {formatTime(panchang.rahu_kaal.start_iso || panchang.rahu_kaal.start)} - {formatTime(panchang.rahu_kaal.end_iso || panchang.rahu_kaal.end)}
                </Typography>
                {panchang.rahu_kaal.duration_minutes && (
                  <Typography variant="caption" color="text.secondary">
                    Duration: {panchang.rahu_kaal.duration_minutes} minutes
                  </Typography>
                )}
                <Typography variant="caption" color="error" display="block" mt={1}>
                  ‚ùå AVOID: New work, meetings, travel, financial transactions
                </Typography>
              </Paper>
            </Grid>
          )}
          {panchang.yamaganda_kaal && (
            <Grid item xs={12} sm={4}>
              <Paper
                elevation={2}
                sx={{
                  p: 2,
                  bgcolor: '#ffebee',
                  border: '2px solid #d32f2f',
                  borderRadius: 2
                }}
              >
                <Box display="flex" alignItems="center" gap={1} mb={1}>
                  <WarningIcon color="error" />
                  <Typography variant="subtitle2" fontWeight="bold" color="error">
                    YAMAGANDA (‡§Ø‡§Æ‡§ó‡§£‡•ç‡§°)
                  </Typography>
                </Box>
                <Typography variant="body1" fontWeight="bold" color="error">
                  {formatTime(panchang.yamaganda_kaal.start_iso || panchang.yamaganda_kaal.start)} - {formatTime(panchang.yamaganda_kaal.end_iso || panchang.yamaganda_kaal.end)}
                </Typography>
                {panchang.yamaganda_kaal.duration_minutes && (
                  <Typography variant="caption" color="text.secondary">
                    Duration: {panchang.yamaganda_kaal.duration_minutes} minutes
                  </Typography>
                )}
              </Paper>
            </Grid>
          )}
          {panchang.gulika_kaal && (
            <Grid item xs={12} sm={4}>
              <Paper
                elevation={2}
                sx={{
                  p: 2,
                  bgcolor: '#ffebee',
                  border: '2px solid #d32f2f',
                  borderRadius: 2
                }}
              >
                <Box display="flex" alignItems="center" gap={1} mb={1}>
                  <WarningIcon color="error" />
                  <Typography variant="subtitle2" fontWeight="bold" color="error">
                    GULIKA (‡§ó‡•Å‡§≤‡§ø‡§ï)
                  </Typography>
                </Box>
                <Typography variant="body1" fontWeight="bold" color="error">
                  {formatTime(panchang.gulika_kaal.start_iso || panchang.gulika_kaal.start)} - {formatTime(panchang.gulika_kaal.end_iso || panchang.gulika_kaal.end)}
                </Typography>
                {panchang.gulika_kaal.duration_minutes && (
                  <Typography variant="caption" color="text.secondary">
                    Duration: {panchang.gulika_kaal.duration_minutes} minutes
                  </Typography>
                )}
              </Paper>
            </Grid>
          )}
        </Grid>

        {/* Auspicious Times */}
        <Typography variant="h6" sx={{ mb: 2, color: '#2e7d32', fontWeight: 'bold' }}>
          ‚úÖ AUSPICIOUS TIMES
        </Typography>
        <Grid container spacing={2} sx={{ mb: 3 }}>
          {panchang.abhijit_muhurat && (
            <Grid item xs={12} sm={6}>
              <Paper
                elevation={2}
                sx={{
                  p: 2,
                  bgcolor: '#e8f5e9',
                  border: '2px solid #2e7d32',
                  borderRadius: 2
                }}
              >
                <Box display="flex" alignItems="center" gap={1} mb={1}>
                  <CheckCircleIcon color="success" />
                  <Typography variant="subtitle2" fontWeight="bold" color="success.main">
                    ABHIJIT MUHURAT (‡§Ö‡§≠‡§ø‡§ú‡§ø‡§§ ‡§Æ‡•Å‡§π‡•Ç‡§∞‡•ç‡§§)
                  </Typography>
                </Box>
                <Typography variant="body1" fontWeight="bold" color="success.main">
                  {formatTime(panchang.abhijit_muhurat.start_iso || panchang.abhijit_muhurat.start)} - {formatTime(panchang.abhijit_muhurat.end_iso || panchang.abhijit_muhurat.end)}
                </Typography>
                {panchang.abhijit_muhurat.duration_minutes && (
                  <Typography variant="caption" color="text.secondary">
                    Duration: {panchang.abhijit_muhurat.duration_minutes} minutes
                  </Typography>
                )}
                <Typography variant="caption" color="success.main" display="block" mt={1}>
                  ‚úÖ BEST FOR: All auspicious activities | Overrides other doshas
                </Typography>
              </Paper>
            </Grid>
          )}
          {panchang.brahma_muhurat && (
            <Grid item xs={12} sm={6}>
              <Paper
                elevation={2}
                sx={{
                  p: 2,
                  bgcolor: '#e8f5e9',
                  border: '2px solid #2e7d32',
                  borderRadius: 2
                }}
              >
                <Box display="flex" alignItems="center" gap={1} mb={1}>
                  <CheckCircleIcon color="success" />
                  <Typography variant="subtitle2" fontWeight="bold" color="success.main">
                    BRAHMA MUHURAT (‡§¨‡•ç‡§∞‡§π‡•ç‡§Æ ‡§Æ‡•Å‡§π‡•Ç‡§∞‡•ç‡§§)
                  </Typography>
                </Box>
                <Typography variant="body1" fontWeight="bold" color="success.main">
                  {formatTime(panchang.brahma_muhurat.start_iso || panchang.brahma_muhurat.start)} - {formatTime(panchang.brahma_muhurat.end_iso || panchang.brahma_muhurat.end)}
                </Typography>
                {panchang.brahma_muhurat.duration_minutes && (
                  <Typography variant="caption" color="text.secondary">
                    Duration: {panchang.brahma_muhurat.duration_minutes} minutes
                  </Typography>
                )}
                <Typography variant="caption" color="success.main" display="block" mt={1}>
                  üåÑ Best for: Meditation, Yoga, Study
                </Typography>
              </Paper>
            </Grid>
          )}
        </Grid>

        {/* Special Days / Day Importance */}
        {panchang.day_importance && panchang.day_importance.is_special && (
          <Paper
            elevation={2}
            sx={{
              p: 2,
              bgcolor: '#fff3cd',
              border: '1px solid #ffc107',
              borderRadius: 2,
              mb: 2
            }}
          >
            <Typography variant="subtitle2" fontWeight="bold" color="warning.main" gutterBottom>
              üéâ SPECIAL OBSERVANCES
            </Typography>
            {panchang.day_importance.special_days && panchang.day_importance.special_days.map((day, idx) => (
              <Box key={idx} mb={1}>
                <Typography variant="body2" fontWeight="medium" color="warning.main">
                  ‚Ä¢ {day.name} ({day.sanskrit})
                </Typography>
                <Typography variant="caption" color="text.secondary">
                  {day.description}
                </Typography>
              </Box>
            ))}
          </Paper>
        )}

        {/* Shubha Kala */}
        {panchang.shubha_kala && (
          <Paper
            elevation={2}
            sx={{
              p: 2,
              bgcolor: '#e8f5e9',
              border: '1px solid #2e7d32',
              borderRadius: 2,
              mb: 2
            }}
          >
            <Typography variant="subtitle2" fontWeight="bold" color="success.main" gutterBottom>
              ‚úÖ SHUBHA KALA (‡§∂‡•Å‡§≠ ‡§ï‡§æ‡§≤)
            </Typography>
            <Grid container spacing={2}>
              {panchang.shubha_kala.morning && (
                <Grid item xs={12} sm={6}>
                  <Typography variant="body2" fontWeight="medium">
                    Morning: {formatTime(panchang.shubha_kala.morning.start_iso || panchang.shubha_kala.morning.start)} - {formatTime(panchang.shubha_kala.morning.end_iso || panchang.shubha_kala.morning.end)}
                  </Typography>
                </Grid>
              )}
              {panchang.shubha_kala.evening && (
                <Grid item xs={12} sm={6}>
                  <Typography variant="body2" fontWeight="medium">
                    Evening: {formatTime(panchang.shubha_kala.evening.start_iso || panchang.shubha_kala.evening.start)} - {formatTime(panchang.shubha_kala.evening.end_iso || panchang.shubha_kala.evening.end)}
                  </Typography>
                </Grid>
              )}
            </Grid>
          </Paper>
        )}
      </CardContent>
    </Card>
  );
};

export default PanchangWidget;
