/**
 * Layout Component
 * Common layout with navigation for all pages
 */

import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
} from '@mui/material';
import { authService } from '../services/authService';

const Layout = ({ children }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const user = authService.getCurrentUser();

  const handleLogout = () => {
    authService.logout();
    navigate('/login');
  };

  return (
    <Box>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1, cursor: 'pointer' }} onClick={() => navigate('/dashboard')}>
            ðŸ›• MandirSync
          </Typography>
          <Button
            color="inherit"
            onClick={() => navigate('/dashboard')}
            sx={{ mr: 1 }}
            variant={location.pathname === '/dashboard' ? 'outlined' : 'text'}
          >
            Dashboard
          </Button>
          <Button
            color="inherit"
            onClick={() => navigate('/donations')}
            sx={{ mr: 1 }}
            variant={location.pathname === '/donations' ? 'outlined' : 'text'}
          >
            Donations
          </Button>
          <Button
            color="inherit"
            onClick={() => navigate('/reports')}
            sx={{ mr: 1 }}
            variant={location.pathname === '/reports' ? 'outlined' : 'text'}
          >
            Reports
          </Button>
          <Button
            color="inherit"
            onClick={() => navigate('/devotees')}
            sx={{ mr: 1 }}
            variant={location.pathname === '/devotees' ? 'outlined' : 'text'}
          >
            Devotees
          </Button>
          <Typography variant="body2" sx={{ mr: 2 }}>
            {user?.full_name || 'User'}
          </Typography>
          <Button color="inherit" onClick={handleLogout}>
            Logout
          </Button>
        </Toolbar>
      </AppBar>
      {children}
    </Box>
  );
};

export default Layout;
