/**
 * Devotee Service
 */

import api from './api';

export const devoteeService = {
  /**
   * Search devotees
   */
  async searchDevotees(query) {
    const response = await api.get('/devotees/search', {
      params: { query },
    });
    return response.data;
  },

  /**
   * Get devotee by phone
   */
  async getDevoteeByPhone(phone) {
    const response = await api.get(`/devotees/phone/${phone}`);
    return response.data;
  },

  /**
   * Create devotee
   */
  async createDevotee(devoteeData) {
    const response = await api.post('/devotees', devoteeData);
    return response.data;
  },

  /**
   * Get all devotees
   */
  async getDevotees(params = {}) {
    const response = await api.get('/devotees', { params });
    return response.data;
  },
};
