/**
 * Donation Form Component
 * Allows staff to record new donations
 */

import React, { useState, useEffect } from 'react';
import {
  Paper,
  TextField,
  Button,
  MenuItem,
  Typography,
  Box,
  Alert,
  CircularProgress,
  Grid,
  FormControl,
  InputLabel,
  Select,
} from '@mui/material';
import { donationService } from '../services/donationService';
import { devoteeService } from '../services/devoteeService';

const DonationForm = ({ onSuccess }) => {
  // Form state
  const [formData, setFormData] = useState({
    devotee_phone: '',
    devotee_name: '',
    devotee_email: '',
    amount: '',
    category_name: '',
    payment_mode: 'cash',
    transaction_id: '',
    cheque_number: '',
    cheque_date: '',
    bank_name: '',
    notes: '',
    is_anonymous: false,
  });

  // UI state
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [categories, setCategories] = useState([]);
  const [categoriesLoading, setCategoriesLoading] = useState(true);
  const [devoteeSuggestions, setDevoteeSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);

  // Load categories on mount
  useEffect(() => {
    loadCategories();
  }, []);

  // Load donation categories
  const loadCategories = async () => {
    try {
      const data = await donationService.getCategories();
      setCategories(data);
      // Set default category if available
      if (data.length > 0 && !formData.category_name) {
        setFormData((prev) => ({ ...prev, category_name: data[0].name }));
      }
    } catch (err) {
      console.error('Failed to load categories:', err);
    }
  };

  // Search devotees by phone
  const searchDevotees = async (phone) => {
    if (phone.length >= 3) {
      try {
        const data = await devoteeService.searchDevotees(phone);
        setDevoteeSuggestions(data);
        setShowSuggestions(data.length > 0);
      } catch (err) {
        // Ignore search errors
      }
    } else {
      setDevoteeSuggestions([]);
      setShowSuggestions(false);
    }
  };

  // Handle devotee phone change
  const handlePhoneChange = (e) => {
    const phone = e.target.value;
    setFormData((prev) => ({ ...prev, devotee_phone: phone }));
    searchDevotees(phone);
  };

  // Select devotee from suggestions
  const selectDevotee = (devotee) => {
    setFormData((prev) => ({
      ...prev,
      devotee_phone: devotee.phone,
      devotee_name: devotee.full_name,
      devotee_email: devotee.email || '',
    }));
    setShowSuggestions(false);
  };

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
    setError('');
    setSuccess(false);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess(false);
    setLoading(true);

    try {
      // Prepare donation data
      const donationData = {
        devotee_phone: formData.devotee_phone,
        devotee_name: formData.devotee_name,
        devotee_email: formData.devotee_email || null,
        amount: parseFloat(formData.amount),
        category_name: formData.category_name,
        payment_mode: formData.payment_mode,
        notes: formData.notes || null,
        is_anonymous: formData.is_anonymous,
      };

      // Add payment-specific fields
      if (formData.payment_mode === 'online' && formData.transaction_id) {
        donationData.transaction_id = formData.transaction_id;
      }

      if (formData.payment_mode === 'cheque') {
        if (formData.cheque_number) donationData.cheque_number = formData.cheque_number;
        if (formData.cheque_date) donationData.cheque_date = formData.cheque_date;
        if (formData.bank_name) donationData.bank_name = formData.bank_name;
      }

      // Submit donation
      const response = await donationService.createDonation(donationData);

      // Success!
      setSuccess(true);
      
      // Reset form
      setFormData({
        devotee_phone: '',
        devotee_name: '',
        devotee_email: '',
        amount: '',
        category_name: categories[0]?.name || '',
        payment_mode: 'cash',
        transaction_id: '',
        cheque_number: '',
        cheque_date: '',
        bank_name: '',
        notes: '',
        is_anonymous: false,
      });

      // Call success callback if provided
      if (onSuccess) {
        onSuccess(response);
      }

      // Clear success message after 3 seconds
      setTimeout(() => setSuccess(false), 3000);
    } catch (err) {
      setError(err.response?.data?.detail || 'Failed to create donation. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Paper elevation={3} sx={{ p: 3 }}>
      <Typography variant="h5" gutterBottom>
        ðŸ’° Record Donation
      </Typography>

      {success && (
        <Alert severity="success" sx={{ mb: 2 }}>
          Donation recorded successfully! Receipt number will be shown in the list.
        </Alert>
      )}

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          {/* Devotee Information */}
          <Grid item xs={12}>
            <Typography variant="subtitle2" sx={{ mb: 1, fontWeight: 'bold' }}>
              Devotee Information
            </Typography>
          </Grid>

          <Grid item xs={12} md={6}>
            <Box sx={{ position: 'relative' }}>
              <TextField
                fullWidth
                label="Phone Number *"
                name="devotee_phone"
                value={formData.devotee_phone}
                onChange={handlePhoneChange}
                required
                inputProps={{ maxLength: 10 }}
                placeholder="9876543210"
              />
              {showSuggestions && devoteeSuggestions.length > 0 && (
                <Paper
                  sx={{
                    position: 'absolute',
                    top: '100%',
                    left: 0,
                    right: 0,
                    zIndex: 1000,
                    mt: 0.5,
                    maxHeight: 200,
                    overflow: 'auto',
                  }}
                >
                  {devoteeSuggestions.map((devotee) => (
                    <Box
                      key={devotee.id}
                      onClick={() => selectDevotee(devotee)}
                      sx={{
                        p: 1.5,
                        cursor: 'pointer',
                        '&:hover': { bgcolor: 'action.hover' },
                      }}
                    >
                      <Typography variant="body2" fontWeight="bold">
                        {devotee.full_name}
                      </Typography>
                      <Typography variant="caption" color="text.secondary">
                        {devotee.phone}
                      </Typography>
                    </Box>
                  ))}
                </Paper>
              )}
            </Box>
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              label="Devotee Name *"
              name="devotee_name"
              value={formData.devotee_name}
              onChange={handleChange}
              required
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              label="Email (Optional)"
              name="devotee_email"
              type="email"
              value={formData.devotee_email}
              onChange={handleChange}
            />
          </Grid>

          {/* Donation Details */}
          <Grid item xs={12} sx={{ mt: 2 }}>
            <Typography variant="subtitle2" sx={{ mb: 1, fontWeight: 'bold' }}>
              Donation Details
            </Typography>
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              label="Amount (â‚¹) *"
              name="amount"
              type="number"
              value={formData.amount}
              onChange={handleChange}
              required
              inputProps={{ min: 1, step: 0.01 }}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth required>
              <InputLabel>Category</InputLabel>
              <Select
                name="category_name"
                value={formData.category_name}
                onChange={handleChange}
                label="Category"
              >
                {categories.map((cat) => (
                  <MenuItem key={cat.id} value={cat.name}>
                    {cat.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth required>
              <InputLabel>Payment Mode</InputLabel>
              <Select
                name="payment_mode"
                value={formData.payment_mode}
                onChange={handleChange}
                label="Payment Mode"
              >
                <MenuItem value="cash">Cash</MenuItem>
                <MenuItem value="card">Card</MenuItem>
                <MenuItem value="upi">UPI</MenuItem>
                <MenuItem value="cheque">Cheque</MenuItem>
                <MenuItem value="online">Online</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          {/* Conditional fields based on payment mode */}
          {formData.payment_mode === 'online' && (
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Transaction ID"
                name="transaction_id"
                value={formData.transaction_id}
                onChange={handleChange}
              />
            </Grid>
          )}

          {formData.payment_mode === 'cheque' && (
            <>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Cheque Number"
                  name="cheque_number"
                  value={formData.cheque_number}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Cheque Date"
                  name="cheque_date"
                  type="date"
                  value={formData.cheque_date}
                  onChange={handleChange}
                  InputLabelProps={{ shrink: true }}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Bank Name"
                  name="bank_name"
                  value={formData.bank_name}
                  onChange={handleChange}
                />
              </Grid>
            </>
          )}

          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Notes (Optional)"
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              multiline
              rows={2}
            />
          </Grid>

          {/* Submit Button */}
          <Grid item xs={12}>
            <Button
              type="submit"
              variant="contained"
              size="large"
              fullWidth
              disabled={loading}
              sx={{ mt: 2 }}
            >
              {loading ? <CircularProgress size={24} /> : 'Record Donation'}
            </Button>
          </Grid>
        </Grid>
      </form>
    </Paper>
  );
};

export default DonationForm;
