/**
 * Panchang Service
 * API calls for Vedic Panchang data
 */

import api from './api';

const panchangService = {
  /**
   * Get today's panchang for a city
   * @param {string} city - City name (e.g., 'Bangalore', 'Kolkata')
   * @returns {Promise} Panchang data
   */
  getTodayPanchang: async (city = 'Bengaluru') => {
    const today = new Date().toISOString().split('T')[0]; // YYYY-MM-DD
    const response = await api.get(`/panchang/`, {
      params: {
        date: today,
        city: city,
      },
    });
    return response.data;
  },

  /**
   * Get panchang for a specific date
   * @param {string} date - Date in YYYY-MM-DD format
   * @param {string} city - City name
   * @returns {Promise} Panchang data
   */
  getPanchang: async (date, city = 'Bangalore') => {
    const response = await api.get(`/panchang/`, {
      params: {
        date: date,
        city: city,
      },
    });
    return response.data;
  },

  /**
   * Get tithi at sunrise (critical for Ekadashi)
   * @param {string} date - Date in YYYY-MM-DD format
   * @param {string} city - City name
   * @returns {Promise} Tithi at sunrise data
   */
  getTithiAtSunrise: async (date, city = 'Bangalore') => {
    const response = await api.get(`/panchang/tithi-at-sunrise`, {
      params: {
        date: date,
        city: city,
      },
    });
    return response.data;
  },

  /**
   * Get available cities
   * @param {string} search - Optional search query
   * @returns {Promise} List of cities
   */
  getCities: async (search = null) => {
    const params = search ? { search } : {};
    const response = await api.get(`/panchang/cities`, { params });
    return response.data;
  },
};

export default panchangService;
