/**
 * Devotees Management Page
 * View, search, and manage devotees
 */

import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  TextField,
  Button,
  CircularProgress,
  Alert,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  IconButton,
  InputAdornment,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Grid,
} from '@mui/material';
import { Search as SearchIcon, Refresh as RefreshIcon, Edit as EditIcon } from '@mui/icons-material';
import Layout from '../components/Layout';
import { devoteeService } from '../services/devoteeService';
import { authService } from '../services/authService';
import { useNavigate } from 'react-router-dom';

const DevoteesPage = () => {
  const navigate = useNavigate();
  const [devotees, setDevotees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredDevotees, setFilteredDevotees] = useState([]);

  // Redirect if not authenticated
  React.useEffect(() => {
    if (!authService.isAuthenticated()) {
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    loadDevotees();
  }, []);

  useEffect(() => {
    // Filter devotees based on search
    if (searchQuery.trim() === '') {
      setFilteredDevotees(devotees);
    } else {
      const query = searchQuery.toLowerCase();
      const filtered = devotees.filter(
        (devotee) =>
          devotee.full_name.toLowerCase().includes(query) ||
          devotee.phone.includes(query) ||
          (devotee.email && devotee.email.toLowerCase().includes(query))
      );
      setFilteredDevotees(filtered);
    }
  }, [searchQuery, devotees]);

  const loadDevotees = async () => {
    try {
      setLoading(true);
      const data = await devoteeService.getDevotees({ limit: 100 });
      setDevotees(data);
      setFilteredDevotees(data);
      setError('');
    } catch (err) {
      setError('Failed to load devotees');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(amount);
  };

  if (loading && devotees.length === 0) {
    return (
      <Layout>
        <Container maxWidth="lg" sx={{ mt: 4 }}>
          <Box display="flex" justifyContent="center" p={4}>
            <CircularProgress />
          </Box>
        </Container>
      </Layout>
    );
  }

  return (
    <Layout>
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
          <Typography variant="h4">ðŸ‘¥ Devotee Management</Typography>
          <IconButton onClick={loadDevotees} title="Refresh">
            <RefreshIcon />
          </IconButton>
        </Box>

        {/* Search Box */}
        <Paper elevation={2} sx={{ p: 2, mb: 3 }}>
          <TextField
            fullWidth
            placeholder="Search by name, phone, or email..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon />
                </InputAdornment>
              ),
            }}
          />
        </Paper>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {filteredDevotees.length === 0 ? (
          <Paper elevation={3} sx={{ p: 4, textAlign: 'center' }}>
            <Typography variant="body1" color="text.secondary">
              {searchQuery
                ? 'No devotees found matching your search'
                : 'No devotees yet. Devotees will be created automatically when you record donations.'}
            </Typography>
          </Paper>
        ) : (
          <>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
              Showing {filteredDevotees.length} of {devotees.length} devotees
            </Typography>
            <TableContainer component={Paper} elevation={3}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Name</TableCell>
                    <TableCell>Phone</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell align="right">Total Donations</TableCell>
                    <TableCell align="right">Donation Value</TableCell>
                    <TableCell align="right">Bookings</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {filteredDevotees.map((devotee) => (
                    <TableRow
                      key={devotee.id}
                      sx={{
                        '&:hover': { bgcolor: 'action.hover' },
                      }}
                    >
                      <TableCell>
                        <Typography variant="body2" fontWeight="bold">
                          {devotee.full_name}
                        </Typography>
                        {devotee.is_vip && (
                          <Chip label="VIP" size="small" color="warning" sx={{ mt: 0.5 }} />
                        )}
                      </TableCell>
                      <TableCell>{devotee.phone}</TableCell>
                      <TableCell>{devotee.email || '-'}</TableCell>
                      <TableCell align="right">
                        <Typography variant="body2" color="primary">
                          {devotee.total_donations || 0}
                        </Typography>
                      </TableCell>
                      <TableCell align="right">
                        <Typography variant="body2" fontWeight="bold" color="success.main">
                          {formatCurrency(devotee.total_donations || 0)}
                        </Typography>
                      </TableCell>
                      <TableCell align="right">
                        <Typography variant="body2">
                          {devotee.total_bookings || 0}
                        </Typography>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </>
        )}
      </Container>
    </Layout>
  );
};

export default DevoteesPage;
