/**
 * Donation List Component
 * Shows recent donations with filters and search
 */

import React, { useState, useEffect } from 'react';
import {
  Paper,
  Typography,
  Box,
  CircularProgress,
  Alert,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  TextField,
  Button,
  IconButton,
  InputAdornment,
  Tooltip,
  Dialog,
} from '@mui/material';
import { Search as SearchIcon, Refresh as RefreshIcon, Receipt as ReceiptIcon, FileDownload as FileDownloadIcon } from '@mui/icons-material';
import ReceiptView from './ReceiptView';
import { donationService } from '../services/donationService';
import { exportDonationsToExcel, exportDonationsToPDF } from '../utils/exportUtils';
import { Menu, MenuItem } from '@mui/material';

const DonationList = ({ refreshKey = 0 }) => {
  const [donations, setDonations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredDonations, setFilteredDonations] = useState([]);
  const [selectedDonation, setSelectedDonation] = useState(null);
  const [receiptOpen, setReceiptOpen] = useState(false);
  const [exportMenuAnchor, setExportMenuAnchor] = useState(null);

  useEffect(() => {
    loadDonations();
  }, [refreshKey]);

  useEffect(() => {
    // Filter donations based on search
    if (searchQuery.trim() === '') {
      setFilteredDonations(donations);
    } else {
      const query = searchQuery.toLowerCase();
      const filtered = donations.filter(
        (donation) =>
          donation.receipt_number.toLowerCase().includes(query) ||
          donation.devotee_name.toLowerCase().includes(query) ||
          donation.devotee_phone.includes(query) ||
          donation.category_name.toLowerCase().includes(query)
      );
      setFilteredDonations(filtered);
    }
  }, [searchQuery, donations]);

  const loadDonations = async () => {
    try {
      setLoading(true);
      const data = await donationService.getDonations({ limit: 50 });
      setDonations(data);
      setFilteredDonations(data);
      setError('');
    } catch (err) {
      setError('Failed to load donations');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-IN', {
      day: '2-digit',
      month: 'short',
      year: 'numeric',
    });
  };

  const getPaymentModeColor = (mode) => {
    const colors = {
      cash: 'success',
      card: 'primary',
      upi: 'info',
      cheque: 'warning',
      online: 'secondary',
    };
    return colors[mode] || 'default';
  };

  if (loading && donations.length === 0) {
    return (
      <Paper sx={{ p: 3 }}>
        <Box display="flex" justifyContent="center" p={3}>
          <CircularProgress />
        </Box>
      </Paper>
    );
  }

  return (
    <Paper elevation={3} sx={{ p: 3 }}>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
        <Typography variant="h6">Recent Donations</Typography>
        <Box>
          <Tooltip title="Export">
            <IconButton
              onClick={(e) => setExportMenuAnchor(e.currentTarget)}
              size="small"
              sx={{ mr: 1 }}
              disabled={filteredDonations.length === 0}
            >
              <FileDownloadIcon />
            </IconButton>
          </Tooltip>
          <Tooltip title="Refresh">
            <IconButton onClick={loadDonations} size="small">
              <RefreshIcon />
            </IconButton>
          </Tooltip>
        </Box>
      </Box>

      {/* Export Menu */}
      <Menu
        anchorEl={exportMenuAnchor}
        open={Boolean(exportMenuAnchor)}
        onClose={() => setExportMenuAnchor(null)}
      >
        <MenuItem
          onClick={() => {
            exportDonationsToExcel(filteredDonations);
            setExportMenuAnchor(null);
          }}
        >
          Export to Excel (.xlsx)
        </MenuItem>
        <MenuItem
          onClick={() => {
            exportDonationsToPDF(filteredDonations);
            setExportMenuAnchor(null);
          }}
        >
          Export to PDF
        </MenuItem>
      </Menu>

      {/* Search Box */}
      <TextField
        fullWidth
        size="small"
        placeholder="Search by receipt, name, phone, or category..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        sx={{ mb: 2 }}
        InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <SearchIcon />
            </InputAdornment>
          ),
        }}
      />

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {filteredDonations.length === 0 ? (
        <Box sx={{ p: 3, textAlign: 'center' }}>
          <Typography variant="body2" color="text.secondary">
            {searchQuery
              ? 'No donations found matching your search'
              : 'No donations yet. Record your first donation!'}
          </Typography>
        </Box>
      ) : (
        <>
          <Typography variant="caption" color="text.secondary" sx={{ mb: 1, display: 'block' }}>
            Showing {filteredDonations.length} of {donations.length} donations
          </Typography>
          <TableContainer sx={{ maxHeight: 500 }}>
            <Table size="small" stickyHeader>
              <TableHead>
                      <TableRow>
                        <TableCell>Receipt</TableCell>
                        <TableCell>Devotee</TableCell>
                        <TableCell align="right">Amount</TableCell>
                        <TableCell>Mode</TableCell>
                        <TableCell>Date</TableCell>
                        <TableCell>Action</TableCell>
                      </TableRow>
              </TableHead>
              <TableBody>
                {filteredDonations.map((donation) => (
                  <TableRow
                    key={donation.id}
                    sx={{
                      '&:hover': { bgcolor: 'action.hover' },
                    }}
                  >
                    <TableCell>
                      <Typography variant="caption" fontWeight="bold" color="primary">
                        {donation.receipt_number}
                      </Typography>
                    </TableCell>
                    <TableCell>
                      <Typography variant="body2">
                        {donation.is_anonymous ? (
                          <em>Anonymous</em>
                        ) : (
                          donation.devotee_name
                        )}
                      </Typography>
                      {!donation.is_anonymous && (
                        <Typography variant="caption" color="text.secondary" display="block">
                          {donation.devotee_phone}
                        </Typography>
                      )}
                    </TableCell>
                    <TableCell align="right">
                      <Typography variant="body2" fontWeight="bold" color="success.main">
                        {formatCurrency(donation.amount)}
                      </Typography>
                    </TableCell>
                    <TableCell>
                      <Chip
                        label={donation.payment_mode.toUpperCase()}
                        size="small"
                        color={getPaymentModeColor(donation.payment_mode)}
                      />
                    </TableCell>
                    <TableCell>
                      <Typography variant="caption">
                        {formatDate(donation.donation_date)}
                      </Typography>
                    </TableCell>
                    <TableCell>
                      <Tooltip title="View Receipt">
                        <IconButton
                          size="small"
                          onClick={() => {
                            setSelectedDonation(donation);
                            setReceiptOpen(true);
                          }}
                        >
                          <ReceiptIcon fontSize="small" />
                        </IconButton>
                      </Tooltip>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </>
      )}

      {/* Receipt Dialog */}
      <Dialog
        open={receiptOpen}
        onClose={() => setReceiptOpen(false)}
        maxWidth="md"
        fullWidth
      >
        {selectedDonation && (
          <ReceiptView
            donation={selectedDonation}
            onClose={() => setReceiptOpen(false)}
          />
        )}
      </Dialog>
    </Paper>
  );
};

export default DonationList;
