/**
 * Receipt View Component
 * Displays donation receipt (can be printed)
 */

import React from 'react';
import {
  Paper,
  Typography,
  Box,
  Button,
  Divider,
  Grid,
} from '@mui/material';
import { Print as PrintIcon } from '@mui/icons-material';

const ReceiptView = ({ donation, onClose }) => {
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-IN', {
      day: '2-digit',
      month: 'long',
      year: 'numeric',
    });
  };

  const handlePrint = () => {
    window.print();
  };

  return (
    <Paper
      elevation={3}
      sx={{
        p: 4,
        maxWidth: 600,
        mx: 'auto',
        '@media print': {
          boxShadow: 'none',
          p: 2,
        },
      }}
    >
      {/* Header */}
      <Box sx={{ textAlign: 'center', mb: 3 }}>
        <Typography variant="h5" fontWeight="bold">
          üõï MandirSync
        </Typography>
        <Typography variant="subtitle2" color="text.secondary">
          Temple Management System
        </Typography>
      </Box>

      <Divider sx={{ my: 2 }} />

      {/* Receipt Details */}
      <Box sx={{ mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          DONATION RECEIPT
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Receipt No: <strong>{donation.receipt_number}</strong>
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Date: {formatDate(donation.donation_date)}
        </Typography>
      </Box>

      <Divider sx={{ my: 2 }} />

      {/* Devotee Information */}
      <Box sx={{ mb: 3 }}>
        <Typography variant="subtitle2" fontWeight="bold" gutterBottom>
          Devotee Details:
        </Typography>
        <Typography variant="body2">
          Name: <strong>{donation.is_anonymous ? 'Anonymous' : donation.devotee_name}</strong>
        </Typography>
        {!donation.is_anonymous && (
          <>
            <Typography variant="body2">
              Phone: {donation.devotee_phone}
            </Typography>
            {donation.devotee_email && (
              <Typography variant="body2">
                Email: {donation.devotee_email}
              </Typography>
            )}
          </>
        )}
      </Box>

      <Divider sx={{ my: 2 }} />

      {/* Donation Details */}
      <Box sx={{ mb: 3 }}>
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <Typography variant="body2" color="text.secondary">
              Category:
            </Typography>
            <Typography variant="body1" fontWeight="bold">
              {donation.category_name}
            </Typography>
          </Grid>
          <Grid item xs={6}>
            <Typography variant="body2" color="text.secondary">
              Payment Mode:
            </Typography>
            <Typography variant="body1" fontWeight="bold">
              {donation.payment_mode.toUpperCase()}
            </Typography>
          </Grid>
          <Grid item xs={12}>
            <Typography variant="body2" color="text.secondary">
              Amount:
            </Typography>
            <Typography variant="h5" color="primary" fontWeight="bold">
              {formatCurrency(donation.amount)}
            </Typography>
          </Grid>
        </Grid>
      </Box>

      {donation.notes && (
        <>
          <Divider sx={{ my: 2 }} />
          <Box>
            <Typography variant="body2" color="text.secondary">
              Notes:
            </Typography>
            <Typography variant="body2">{donation.notes}</Typography>
          </Box>
        </>
      )}

      <Divider sx={{ my: 3 }} />

      {/* Footer */}
      <Box sx={{ textAlign: 'center', mt: 3 }}>
        <Typography variant="caption" color="text.secondary">
          This is a computer-generated receipt. No signature required.
        </Typography>
        <Typography variant="caption" color="text.secondary" display="block" sx={{ mt: 1 }}>
          Thank you for your generous donation! üôè
        </Typography>
      </Box>

      {/* Print Button (hidden when printing) */}
      <Box sx={{ mt: 3, textAlign: 'center', '@media print': { display: 'none' } }}>
        <Button
          variant="contained"
          startIcon={<PrintIcon />}
          onClick={handlePrint}
          sx={{ mr: 2 }}
        >
          Print Receipt
        </Button>
        {onClose && (
          <Button variant="outlined" onClick={onClose}>
            Close
          </Button>
        )}
      </Box>
    </Paper>
  );
};

export default ReceiptView;
