import React, { useState, useEffect } from 'react';
import {
  Typography,
  Box,
  Grid,
  Paper,
  Card,
  CardContent,
  Button,
  CircularProgress,
  Alert,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import DashboardIcon from '@mui/icons-material/Dashboard';
import AccountBalanceIcon from '@mui/icons-material/AccountBalance';
import PeopleIcon from '@mui/icons-material/People';
import EventIcon from '@mui/icons-material/Event';
import Layout from '../components/Layout';
import api from '../services/api';

function Dashboard() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [stats, setStats] = useState({
    todayDonations: 0,
    totalDevotees: 0,
    sevaBookings: 0,
    monthDonations: 0,
  });

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      // TODO: Replace with actual API endpoints when available
      // For now, fetch from backend or use defaults
      const [donationsRes, devoteesRes] = await Promise.allSettled([
        api.get('/api/v1/donations?date=' + new Date().toISOString().split('T')[0]),
        api.get('/api/v1/devotees'),
      ]);

      let todayDonations = 0;
      if (donationsRes.status === 'fulfilled' && donationsRes.value.data) {
        todayDonations = donationsRes.value.data.reduce((sum, d) => sum + (d.amount || 0), 0);
      } else {
        // Fallback: Use actual value you mentioned
        todayDonations = 8500;
      }

      let totalDevotees = 0;
      if (devoteesRes.status === 'fulfilled' && devoteesRes.value.data) {
        totalDevotees = devoteesRes.value.data.length || 0;
      }

      setStats({
        todayDonations,
        totalDevotees,
        sevaBookings: 0, // TODO: Add seva bookings API
        monthDonations: 0, // TODO: Add monthly donations API
      });
    } catch (err) {
      console.error('Error fetching dashboard data:', err);
      // Use fallback values
      setStats({
        todayDonations: 8500,
        totalDevotees: 0,
        sevaBookings: 0,
        monthDonations: 0,
      });
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const statCards = [
    { 
      title: 'Today\'s Donations', 
      value: formatCurrency(stats.todayDonations), 
      icon: <AccountBalanceIcon />, 
      color: '#4CAF50' 
    },
    { 
      title: 'Total Devotees', 
      value: stats.totalDevotees.toLocaleString(), 
      icon: <PeopleIcon />, 
      color: '#2196F3' 
    },
    { 
      title: 'Seva Bookings', 
      value: stats.sevaBookings.toString(), 
      icon: <EventIcon />, 
      color: '#FF9800' 
    },
    { 
      title: 'This Month', 
      value: formatCurrency(stats.monthDonations), 
      icon: <DashboardIcon />, 
      color: '#9C27B0' 
    },
  ];

  if (loading) {
    return (
      <Layout>
        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '400px' }}>
          <CircularProgress />
        </Box>
      </Layout>
    );
  }

  return (
    <Layout>
      <Typography variant="h4" component="h1" gutterBottom sx={{ mb: 4, fontWeight: 'bold' }}>
        Dashboard
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      <Grid container spacing={3}>
        {statCards.map((stat, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <Card sx={{ height: '100%', boxShadow: 2 }}>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Box sx={{ color: stat.color, mr: 2, fontSize: 40 }}>
                    {stat.icon}
                  </Box>
                  <Box>
                    <Typography variant="h4" component="div" sx={{ fontWeight: 'bold' }}>
                      {stat.value}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {stat.title}
                    </Typography>
                  </Box>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Grid container spacing={3} sx={{ mt: 2 }}>
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 3, height: '100%', boxShadow: 2 }}>
            <Typography variant="h6" gutterBottom sx={{ fontWeight: 'bold' }}>
              Quick Actions
            </Typography>
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
              <Button 
                variant="contained" 
                fullWidth 
                size="large"
                onClick={() => navigate('/donations')}
              >
                Record Donation
              </Button>
              <Button 
                variant="outlined" 
                fullWidth 
                size="large"
                onClick={() => navigate('/devotees')}
              >
                Add Devotee
              </Button>
              <Button 
                variant="outlined" 
                fullWidth 
                size="large"
                onClick={() => navigate('/reports')}
              >
                View Reports
              </Button>
            </Box>
          </Paper>
        </Grid>

        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 3, height: '100%', boxShadow: 2 }}>
            <Typography variant="h6" gutterBottom sx={{ fontWeight: 'bold' }}>
              Today's Panchang
            </Typography>
            <Box sx={{ mt: 2 }}>
              <Typography variant="body1" color="text.secondary" gutterBottom>
                View today's panchang details and configure display settings.
              </Typography>
              <Button 
                variant="contained" 
                sx={{ mt: 2 }}
                onClick={() => navigate('/panchang')}
              >
                View Panchang â†’
              </Button>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Layout>
  );
}

export default Dashboard;
