import React, { useState, useEffect } from 'react';
import {
  Typography,
  Box,
  Paper,
  Grid,
  Card,
  CardContent,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  MenuItem,
  CircularProgress,
  Alert,
} from '@mui/material';
import GetAppIcon from '@mui/icons-material/GetApp';
import DownloadIcon from '@mui/icons-material/Download';
import PictureAsPdfIcon from '@mui/icons-material/PictureAsPdf';
import Layout from '../components/Layout';
import api from '../services/api';

function Reports() {
  const [loading, setLoading] = useState(false);
  const [dateFrom, setDateFrom] = useState(new Date(new Date().setDate(1))); // First day of month
  const [dateTo, setDateTo] = useState(new Date());
  const [reportType, setReportType] = useState('daily');
  const [reportData, setReportData] = useState(null);
  const [error, setError] = useState('');

  const reportTypes = [
    { value: 'daily', label: 'Daily Collection' },
    { value: 'monthly', label: 'Monthly Summary' },
    { value: 'category', label: 'Category-wise' },
    { value: 'devotee', label: 'Devotee-wise' },
  ];

  const fetchReport = async () => {
    try {
      setLoading(true);
      setError('');
      
      let response;
      if (reportType === 'daily') {
        response = await api.get(`/api/v1/donations/report/daily?date=${dateTo.toISOString().split('T')[0]}`);
      } else if (reportType === 'monthly') {
        response = await api.get(`/api/v1/donations/report/monthly?month=${dateFrom.getMonth() + 1}&year=${dateFrom.getFullYear()}`);
      } else {
        response = await api.get(`/api/v1/donations?date_from=${dateFrom.toISOString().split('T')[0]}&date_to=${dateTo.toISOString().split('T')[0]}`);
      }
      
      setReportData(response.data);
    } catch (err) {
      console.error('Error fetching report:', err);
      setError('Failed to load report data');
      // Use mock data for demonstration
      setReportData({
        total: 8500,
        count: 5,
        by_category: [
          { category: 'General Donation', amount: 3000, count: 2 },
          { category: 'Annadanam', amount: 2000, count: 1 },
          { category: 'Construction Fund', amount: 1500, count: 1 },
          { category: 'Corpus Fund', amount: 2000, count: 1 },
        ],
        donations: []
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchReport();
  }, [reportType]);

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const handleDownloadExcel = async () => {
    try {
      setError('');
      const response = await api.get('/api/v1/donations/export/excel', {
        params: {
          date_from: dateFrom.toISOString().split('T')[0],
          date_to: dateTo.toISOString().split('T')[0],
        },
        responseType: 'blob',
      });
      
      // Handle CSV response
      const blob = new Blob([response.data], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `donations_${dateFrom.toISOString().split('T')[0]}_to_${dateTo.toISOString().split('T')[0]}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error('Error downloading Excel:', err);
      setError(err.response?.data?.detail || 'Failed to download Excel file. Please try again.');
    }
  };

  const handleDownloadPDF = async () => {
    try {
      setError('');
      // Create a printable version of the report
      const printWindow = window.open('', '_blank');
      if (!printWindow) {
        setError('Please allow popups to download PDF');
        return;
      }
      
      printWindow.document.write(`
        <html>
          <head>
            <title>Donation Report</title>
            <style>
              body { font-family: Arial, sans-serif; padding: 20px; }
              h1 { color: #FF9933; }
              table { width: 100%; border-collapse: collapse; margin-top: 20px; }
              th, td { border: 1px solid #ddd; padding: 8px; text-align: left; }
              th { background-color: #FF9933; color: white; }
              .total { font-weight: bold; font-size: 18px; margin-top: 20px; }
            </style>
          </head>
          <body>
            <h1>Donation Report</h1>
            <p><strong>Period:</strong> ${dateFrom.toLocaleDateString()} to ${dateTo.toLocaleDateString()}</p>
            ${reportData ? `
              <p><strong>Total Collection:</strong> ${formatCurrency(reportData.total || 0)}</p>
              <p><strong>Total Donations:</strong> ${reportData.count || 0}</p>
              <table>
                <thead>
                  <tr>
                    <th>Category</th>
                    <th>Count</th>
                    <th>Amount</th>
                  </tr>
                </thead>
                <tbody>
                  ${reportData.by_category?.map(item => `
                    <tr>
                      <td>${item.category}</td>
                      <td>${item.count}</td>
                      <td>${formatCurrency(item.amount)}</td>
                    </tr>
                  `).join('') || ''}
                </tbody>
              </table>
            ` : '<p>No data available</p>'}
          </body>
        </html>
      `);
      printWindow.document.close();
      printWindow.print();
    } catch (err) {
      console.error('Error generating PDF:', err);
      setError('Failed to generate PDF');
    }
  };

  return (
    <Layout>
      <Typography variant="h4" component="h1" gutterBottom sx={{ fontWeight: 'bold' }}>
        Reports
      </Typography>

      <Paper sx={{ p: 3, mt: 2 }}>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              select
              label="Report Type"
              value={reportType}
              onChange={(e) => setReportType(e.target.value)}
            >
              {reportTypes.map((type) => (
                <MenuItem key={type.value} value={type.value}>
                  {type.label}
                </MenuItem>
              ))}
            </TextField>
          </Grid>
          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              type="date"
              label="From Date"
              value={dateFrom.toISOString().split('T')[0]}
              onChange={(e) => setDateFrom(new Date(e.target.value))}
              InputLabelProps={{ shrink: true }}
            />
          </Grid>
          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              type="date"
              label="To Date"
              value={dateTo.toISOString().split('T')[0]}
              onChange={(e) => setDateTo(new Date(e.target.value))}
              InputLabelProps={{ shrink: true }}
            />
          </Grid>
          <Grid item xs={12} sm={2}>
            <Button
              fullWidth
              variant="contained"
              onClick={fetchReport}
              disabled={loading}
              startIcon={loading ? <CircularProgress size={20} /> : <GetAppIcon />}
            >
              Generate Report
            </Button>
          </Grid>
          <Grid item xs={12} sm={2}>
            <Button
              fullWidth
              variant="outlined"
              onClick={handleDownloadExcel}
              disabled={!reportData || loading}
              startIcon={<DownloadIcon />}
            >
              Excel
            </Button>
          </Grid>
          <Grid item xs={12} sm={2}>
            <Button
              fullWidth
              variant="outlined"
              onClick={handleDownloadPDF}
              disabled={!reportData || loading}
              startIcon={<PictureAsPdfIcon />}
            >
              PDF
            </Button>
          </Grid>
        </Grid>
      </Paper>

      {error && (
        <Alert severity="error" sx={{ mt: 2 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
          <CircularProgress />
        </Box>
      ) : reportData ? (
        <Grid container spacing={3} sx={{ mt: 2 }}>
          <Grid item xs={12} md={4}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Total Collection
                </Typography>
                <Typography variant="h4" sx={{ fontWeight: 'bold', color: 'primary.main' }}>
                  {formatCurrency(reportData.total || 0)}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {reportData.count || 0} donations
                </Typography>
              </CardContent>
            </Card>
          </Grid>

          <Grid item xs={12} md={8}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                Category-wise Breakdown
              </Typography>
              <TableContainer>
                <Table size="small">
                  <TableHead>
                    <TableRow>
                      <TableCell>Category</TableCell>
                      <TableCell align="right">Count</TableCell>
                      <TableCell align="right">Amount</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {reportData.by_category?.map((item, index) => (
                      <TableRow key={index}>
                        <TableCell>{item.category}</TableCell>
                        <TableCell align="right">{item.count}</TableCell>
                        <TableCell align="right">{formatCurrency(item.amount)}</TableCell>
                      </TableRow>
                    ))}
                    {(!reportData.by_category || reportData.by_category.length === 0) && (
                      <TableRow>
                        <TableCell colSpan={3} align="center">
                          No data available
                        </TableCell>
                      </TableRow>
                    )}
                  </TableBody>
                </Table>
              </TableContainer>
            </Paper>
          </Grid>
        </Grid>
      ) : (
        <Paper sx={{ p: 3, mt: 2, textAlign: 'center' }}>
          <Typography variant="body1" color="text.secondary">
            Select report type and date range, then click "Generate Report"
          </Typography>
        </Paper>
      )}
    </Layout>
  );
}

export default Reports;
