/**
 * Export Utilities
 * Functions to export data to Excel and PDF
 */

import * as XLSX from 'xlsx';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

/**
 * Export donations to Excel
 */
export const exportDonationsToExcel = donations => {
  // Prepare data
  const data = donations.map(donation => ({
    'Receipt Number': donation.receipt_number,
    'Date': donation.donation_date,
    'Devotee Name': donation.is_anonymous ? 'Anonymous' : donation.devotee_name,
    'Phone': donation.devotee_phone || '',
    'Email': donation.devotee_email || '',
    'Category': donation.category_name,
    'Amount (₹)': donation.amount,
    'Payment Mode': donation.payment_mode.toUpperCase(),
    'Transaction ID': donation.transaction_id || '',
    'Notes': donation.notes || ''
  }));

  // Create workbook
  const wb = XLSX.utils.book_new();
  const ws = XLSX.utils.json_to_sheet(data);

  // Set column widths
  ws['!cols'] = [{
    wch: 18
  },
  // Receipt Number
  {
    wch: 12
  },
  // Date
  {
    wch: 25
  },
  // Devotee Name
  {
    wch: 12
  },
  // Phone
  {
    wch: 25
  },
  // Email
  {
    wch: 20
  },
  // Category
  {
    wch: 12
  },
  // Amount
  {
    wch: 12
  },
  // Payment Mode
  {
    wch: 20
  },
  // Transaction ID
  {
    wch: 30
  } // Notes
  ];

  // Add worksheet to workbook
  XLSX.utils.book_append_sheet(wb, ws, 'Donations');

  // Generate filename
  const filename = `donations_${new Date().toISOString().split('T')[0]}.xlsx`;

  // Write file
  XLSX.writeFile(wb, filename);
};

/**
 * Export donations to PDF
 */
export const exportDonationsToPDF = donations => {
  const doc = new jsPDF();

  // Title
  doc.setFontSize(18);
  doc.text('Donation Report', 14, 20);
  doc.setFontSize(10);
  doc.text(`Generated on: ${new Date().toLocaleDateString('en-IN')}`, 14, 28);
  doc.text(`Total Donations: ${donations.length}`, 14, 34);

