  // Add table
  doc.autoTable({
    head: [['Receipt', 'Date', 'Devotee', 'Category', 'Amount', 'Mode']],
    body: tableData,
    startY: 40,
    styles: {
      fontSize: 8
    },
    headStyles: {
      fillColor: [25, 118, 210]
    }
