/**
 * Reports Page
 * Shows daily and monthly collection reports with charts
 */

import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Grid,
  TextField,
  Button,
  CircularProgress,
  Alert,
  Card,
  CardContent,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  Tabs,
  Tab,
} from '@mui/material';
import Layout from '../components/Layout';
import { donationService } from '../services/donationService';
import { authService } from '../services/authService';
import { useNavigate } from 'react-router-dom';

const ReportsPage = () => {
  const navigate = useNavigate();
  const [tabValue, setTabValue] = useState(0);
  const [reportDate, setReportDate] = useState(new Date().toISOString().split('T')[0]);
  const [monthYear, setMonthYear] = useState(
    `${new Date().getFullYear()}-${String(new Date().getMonth() + 1).padStart(2, '0')}`
  );
  const [report, setReport] = useState(null);
  const [monthlyReport, setMonthlyReport] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Redirect if not authenticated
  React.useEffect(() => {
    if (!authService.isAuthenticated()) {
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    if (tabValue === 0) {
      loadDailyReport();
    } else {
      loadMonthlyReport();
    }
  }, [tabValue]);

  const loadDailyReport = async () => {
    try {
      setLoading(true);
      setError('');
      const data = await donationService.getDailyReport(reportDate);
      setReport(data);
    } catch (err) {
      setError('Failed to load daily report');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const loadMonthlyReport = async () => {
    try {
      setLoading(true);
      setError('');
      // Get all donations for the month
      const [year, month] = monthYear.split('-');
      const startDate = `${year}-${month}-01`;
      const endDate = `${year}-${month}-${new Date(year, month, 0).getDate()}`;
      
      const donations = await donationService.getDonations({
        date_from: startDate,
        date_to: endDate,
        limit: 1000,
      });

      // Calculate monthly totals
      const totalAmount = donations.reduce((sum, d) => sum + d.amount, 0);
      const totalCount = donations.length;
      
      // Group by category
      const byCategory = {};
      donations.forEach((d) => {
        const cat = d.category_name || 'Unknown';
        if (!byCategory[cat]) {
          byCategory[cat] = { count: 0, amount: 0 };
        }
        byCategory[cat].count++;
        byCategory[cat].amount += d.amount;
      });

      // Group by payment mode
      const byPayment = {};
      donations.forEach((d) => {
        const mode = d.payment_mode || 'unknown';
        if (!byPayment[mode]) {
          byPayment[mode] = { count: 0, amount: 0 };
        }
        byPayment[mode].count++;
        byPayment[mode].amount += d.amount;
      });

      setMonthlyReport({
        month: monthYear,
        total_amount: totalAmount,
        total_donations: totalCount,
        by_category: Object.entries(byCategory).map(([cat, data]) => ({
          category: cat,
          count: data.count,
          amount: data.amount,
        })),
        by_payment_mode: Object.entries(byPayment).map(([mode, data]) => ({
          mode,
          count: data.count,
          amount: data.amount,
        })),
      });
    } catch (err) {
      setError('Failed to load monthly report');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatMonthYear = (my) => {
    const [year, month] = my.split('-');
    const date = new Date(year, parseInt(month) - 1);
    return date.toLocaleDateString('en-IN', { month: 'long', year: 'numeric' });
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const renderDailyReport = () => (
    <>
      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} md={4}>
            <TextField
              fullWidth
              label="Select Date"
              type="date"
              value={reportDate}
              onChange={(e) => setReportDate(e.target.value)}
              InputLabelProps={{ shrink: true }}
            />
          </Grid>
          <Grid item xs={12} md={2}>
            <Button
              fullWidth
              variant="contained"
              onClick={loadDailyReport}
              disabled={loading}
            >
              {loading ? <CircularProgress size={24} /> : 'Load Report'}
            </Button>
          </Grid>
        </Grid>
      </Paper>

      {report && (
        <>
          <Grid container spacing={3} sx={{ mb: 3 }}>
            <Grid item xs={12} md={4}>
              <Card>
                <CardContent>
                  <Typography color="text.secondary" gutterBottom>
                    Total Collection
                  </Typography>
                  <Typography variant="h4" color="primary">
                    {formatCurrency(report.total_amount)}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} md={4}>
              <Card>
                <CardContent>
                  <Typography color="text.secondary" gutterBottom>
                    Total Donations
                  </Typography>
                  <Typography variant="h4" color="primary">
                    {report.total_donations}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} md={4}>
              <Card>
                <CardContent>
                  <Typography color="text.secondary" gutterBottom>
                    Average Donation
                  </Typography>
                  <Typography variant="h4" color="primary">
                    {formatCurrency(
                      report.total_donations > 0
                        ? report.total_amount / report.total_donations
                        : 0
                    )}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          </Grid>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Paper elevation={3} sx={{ p: 3 }}>
                <Typography variant="h6" gutterBottom>
                  By Category
                </Typography>
                {report.by_category.length > 0 ? (
                  <TableContainer>
                    <Table size="small">
                      <TableHead>
                        <TableRow>
                          <TableCell>Category</TableCell>
                          <TableCell align="right">Count</TableCell>
                          <TableCell align="right">Amount</TableCell>
                          <TableCell align="right">%</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {report.by_category.map((item, index) => (
                          <TableRow key={index}>
                            <TableCell>
                              <Chip label={item.category} size="small" />
                            </TableCell>
                            <TableCell align="right">{item.count}</TableCell>
                            <TableCell align="right">
                              {formatCurrency(item.amount)}
                            </TableCell>
                            <TableCell align="right">
                              {report.total_amount > 0
                                ? ((item.amount / report.total_amount) * 100).toFixed(1)
                                : 0}
                              %
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                ) : (
                  <Typography variant="body2" color="text.secondary">
                    No donations in this category
                  </Typography>
                )}
              </Paper>
            </Grid>

            <Grid item xs={12} md={6}>
              <Paper elevation={3} sx={{ p: 3 }}>
                <Typography variant="h6" gutterBottom>
                  By Payment Mode
                </Typography>
                {report.by_payment_mode.length > 0 ? (
                  <TableContainer>
                    <Table size="small">
                      <TableHead>
                        <TableRow>
                          <TableCell>Mode</TableCell>
                          <TableCell align="right">Count</TableCell>
                          <TableCell align="right">Amount</TableCell>
                          <TableCell align="right">%</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {report.by_payment_mode.map((item, index) => (
                          <TableRow key={index}>
                            <TableCell>
                              <Chip
                                label={item.mode.toUpperCase()}
                                size="small"
                                color={
                                  item.mode === 'cash'
                                    ? 'success'
                                    : item.mode === 'upi'
                                    ? 'info'
                                    : 'primary'
                                }
                              />
                            </TableCell>
                            <TableCell align="right">{item.count}</TableCell>
                            <TableCell align="right">
                              {formatCurrency(item.amount)}
                            </TableCell>
                            <TableCell align="right">
                              {report.total_amount > 0
                                ? ((item.amount / report.total_amount) * 100).toFixed(1)
                                : 0}
                              %
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                ) : (
                  <Typography variant="body2" color="text.secondary">
                    No payments recorded
                  </Typography>
                )}
              </Paper>
            </Grid>
          </Grid>
        </>
      )}
    </>
  );

  const renderMonthlyReport = () => (
    <>
      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} md={4}>
            <TextField
              fullWidth
              label="Select Month"
              type="month"
              value={monthYear}
              onChange={(e) => setMonthYear(e.target.value)}
              InputLabelProps={{ shrink: true }}
            />
          </Grid>
          <Grid item xs={12} md={2}>
            <Button
              fullWidth
              variant="contained"
              onClick={loadMonthlyReport}
              disabled={loading}
            >
              {loading ? <CircularProgress size={24} /> : 'Load Report'}
            </Button>
          </Grid>
        </Grid>
      </Paper>

      {monthlyReport && (
        <>
          <Typography variant="h5" gutterBottom sx={{ mb: 3 }}>
            {formatMonthYear(monthlyReport.month)}
          </Typography>

          <Grid container spacing={3} sx={{ mb: 3 }}>
            <Grid item xs={12} md={4}>
              <Card>
                <CardContent>
                  <Typography color="text.secondary" gutterBottom>
                    Total Collection
                  </Typography>
                  <Typography variant="h4" color="primary">
                    {formatCurrency(monthlyReport.total_amount)}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} md={4}>
              <Card>
                <CardContent>
                  <Typography color="text.secondary" gutterBottom>
                    Total Donations
                  </Typography>
                  <Typography variant="h4" color="primary">
                    {monthlyReport.total_donations}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} md={4}>
              <Card>
                <CardContent>
                  <Typography color="text.secondary" gutterBottom>
                    Average Donation
                  </Typography>
                  <Typography variant="h4" color="primary">
                    {formatCurrency(
                      monthlyReport.total_donations > 0
                        ? monthlyReport.total_amount / monthlyReport.total_donations
                        : 0
                    )}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          </Grid>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Paper elevation={3} sx={{ p: 3 }}>
                <Typography variant="h6" gutterBottom>
                  By Category
                </Typography>
                {monthlyReport.by_category.length > 0 ? (
                  <TableContainer>
                    <Table size="small">
                      <TableHead>
                        <TableRow>
                          <TableCell>Category</TableCell>
                          <TableCell align="right">Count</TableCell>
                          <TableCell align="right">Amount</TableCell>
                          <TableCell align="right">%</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {monthlyReport.by_category.map((item, index) => (
                          <TableRow key={index}>
                            <TableCell>
                              <Chip label={item.category} size="small" />
                            </TableCell>
                            <TableCell align="right">{item.count}</TableCell>
                            <TableCell align="right">
                              {formatCurrency(item.amount)}
                            </TableCell>
                            <TableCell align="right">
                              {monthlyReport.total_amount > 0
                                ? ((item.amount / monthlyReport.total_amount) * 100).toFixed(1)
                                : 0}
                              %
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                ) : (
                  <Typography variant="body2" color="text.secondary">
                    No donations in this month
                  </Typography>
                )}
              </Paper>
            </Grid>

            <Grid item xs={12} md={6}>
              <Paper elevation={3} sx={{ p: 3 }}>
                <Typography variant="h6" gutterBottom>
                  By Payment Mode
                </Typography>
                {monthlyReport.by_payment_mode.length > 0 ? (
                  <TableContainer>
                    <Table size="small">
                      <TableHead>
                        <TableRow>
                          <TableCell>Mode</TableCell>
                          <TableCell align="right">Count</TableCell>
                          <TableCell align="right">Amount</TableCell>
                          <TableCell align="right">%</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {monthlyReport.by_payment_mode.map((item, index) => (
                          <TableRow key={index}>
                            <TableCell>
                              <Chip
                                label={item.mode.toUpperCase()}
                                size="small"
                                color={
                                  item.mode === 'cash'
                                    ? 'success'
                                    : item.mode === 'upi'
                                    ? 'info'
                                    : 'primary'
                                }
                              />
                            </TableCell>
                            <TableCell align="right">{item.count}</TableCell>
                            <TableCell align="right">
                              {formatCurrency(item.amount)}
                            </TableCell>
                            <TableCell align="right">
                              {monthlyReport.total_amount > 0
                                ? ((item.amount / monthlyReport.total_amount) * 100).toFixed(1)
                                : 0}
                              %
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                ) : (
                  <Typography variant="body2" color="text.secondary">
                    No payments recorded
                  </Typography>
                )}
              </Paper>
            </Grid>
          </Grid>
        </>
      )}
    </>
  );

  if (loading && !report && !monthlyReport) {
    return (
      <Layout>
        <Container maxWidth="lg" sx={{ mt: 4 }}>
          <Box display="flex" justifyContent="center" p={4}>
            <CircularProgress />
          </Box>
        </Container>
      </Layout>
    );
  }

  return (
    <Layout>
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h4" gutterBottom>
           Reports
        </Typography>

        <Paper elevation={2} sx={{ mb: 3 }}>
          <Tabs value={tabValue} onChange={handleTabChange}>
            <Tab label="Daily Report" />
            <Tab label="Monthly Report" />
          </Tabs>
        </Paper>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {tabValue === 0 ? renderDailyReport() : renderMonthlyReport()}
      </Container>
    </Layout>
  );
};

export default ReportsPage;
